import javax.jms.*;
import org.apache.activemq.ActiveMQConnectionFactory;

public class ActiveMqConsumer implements Runnable, ExceptionListener {
    private Connection connection;
    private Session session;
    private MessageConsumer consumer;
    private String remoteUri;
    private String queueName;

    public ActiveMqConsumer(String remoteUri, String queueName) {
        this.remoteUri = remoteUri;
        this.queueName = queueName;
    }

    public void start() throws JMSException {
        ActiveMQConnectionFactory factory = new ActiveMQConnectionFactory(remoteUri);
        connection = factory.createConnection();
        connection.setExceptionListener(this);
        connection.start();
        session = connection.createSession(false, Session.AUTO_ACKNOWLEDGE);
        Destination destination = session.createQueue(queueName);
        consumer = session.createConsumer(destination);
    }

    public void stop() throws JMSException {
        if (consumer != null) consumer.close();
        if (session != null) session.close();
        if (connection != null) connection.close();
    }

    @Override
    public void run() {
        try {
            while (true) {
                Message message = consumer.receive();
                if (message instanceof TextMessage) {
                    TextMessage textMessage = (TextMessage) message;
                    System.out.println("Received text message: " + textMessage.getText());
                } else if (message instanceof BytesMessage) {
                    BytesMessage bytesMessage = (BytesMessage) message;
                    byte[] data = new byte[(int) bytesMessage.getBodyLength()];
                    bytesMessage.readBytes(data);
                    System.out.println("Received bytes message: " + new String(data));
                }
            }
        } catch (JMSException e) {
            e.printStackTrace();
        }
    }

    @Override
    public void onException(JMSException e) {
        System.err.println("JMS Exception occurred. Stopping consumer.");
        try {
            stop();
        } catch (JMSException ex) {
            ex.printStackTrace();
        }
    }

    public Connection getConnection() {
        return connection;
    }

    public Session getSession() {
        return session;
    }

    public MessageConsumer getConsumer() {
