    public boolean isOpen() {
        return mChannel != null && mChannel.isActive();
    }

    public void close() throws Exception {
        if (mChannel != null) {
            mChannel.close().sync();
        }
        if (mGroup != null) {
            mGroup.shutdownGracefully(0, 0, TimeUnit.SECONDS).sync();
        }
    }

    private int validateURIAndGetPort(URI uri) {
        if (uri.getPort() == -1) {
            return uri.getScheme().equals("wss") ? 443 : 80;
        }
        return uri.getPort();
    }

    private SslContext getSSLContext(String scheme) throws Exception {
        if (scheme.equals("wss")) {
            return SslContextBuilder.forClient()
                    .trustManager(InsecureTrustManagerFactory.INSTANCE)
                    .build();
        }
        return null;
    }

    private ByteBuf toByteBuf(byte[] bytes) {
        return Unpooled.wrappedBuffer(bytes);
    }
