import io.netty.bootstrap.Bootstrap;
import io.netty.channel.*;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.SocketChannel;
import io.netty.channel.socket.nio.NioSocketChannel;
import io.netty.handler.codec.string.StringDecoder;
import io.netty.handler.codec.string.StringEncoder;
import io.netty.handler.ssl.SslContext;
import io.netty.handler.ssl.SslHandler;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import javax.net.ssl.SSLContext;
import java.util.concurrent.TimeUnit;

public class ProxyClientContainer {

    private final Logger logger = LoggerFactory.getLogger(ProxyClientContainer.class);
    private NioEventLoopGroup workerGroup;
    private Bootstrap bootstrap;
    private Bootstrap realServerBootstrap;
    private Config config;
    private SslContext sslContext;
    private long sleepTimeMill;

    public ProxyClientContainer() {
        workerGroup = new NioEventLoopGroup();
        bootstrap = new Bootstrap();
        realServerBootstrap = new Bootstrap();
        config = new Config();
        sslContext = null;
        sleepTimeMill = 100; // Initial sleep time for reconnection

        bootstrap.group(workerGroup)
                .channel(NioSocketChannel.class)
                .handler(new ChannelInitializer<SocketChannel>() {
                    @Override
                    protected void initChannel(SocketChannel ch) throws Exception {
                        ChannelPipeline pipeline = ch.pipeline();
                        if (sslContext != null) {
                            pipeline.addLast(createSslHandler(sslContext));
                        }
                        pipeline.addLast(new StringDecoder());
                        pipeline.addLast(new StringEncoder());
                        pipeline.addLast(new ProxyClientHandler());
                    }
                });
    }

    public void start() {
        connectProxyServer();
    }

    private ChannelHandler createSslHandler(SslContext sslContext) {
        return new SslHandler(sslContext.newEngine(new InetSocketAddress(config.getProxyHost(), config.getProxyPort())));
    }

    private void connectProxyServer() {
        bootstrap.connect(config.getProxyHost(), config.getProxyPort()).addListener(future -> {
            if (future.isSuccess()) {
                logger.info("Connected to proxy server");
            } else {
                logger.error("Failed to connect to proxy server", future.cause());
                reconnectWait();
            }
        });
    }

    public void stop() {
        workerGroup.shutdownGracefully();
    }

    private void channelInactive(ChannelHandlerContext ctx) {
        logger.warn("Connection to proxy server lost, attempting to reconnect...");
        reconnectWait();
    }

    private void reconnectWait() {
        try {
            TimeUnit.MILLISECONDS.sleep(sleepTimeMill);
            sleepTimeMill *= 2; // Exponential backoff
            connectProxyServer();
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
            logger.error("Reconnection interrupted", e);
        }
    }

    private static class ProxyClientHandler extends ChannelInboundHandlerAdapter {
        @Override
        public void channelInactive(ChannelHandlerContext ctx) throws Exception {
            ((ProxyClientContainer) ctx.pipeline().last().parent().parent()).channelInactive(ctx);
            super.channelInactive(ctx);
        }
    }
}
