import io.netty.channel.Channel;
import io.netty.channel.ChannelFuture;
import io.netty.channel.ChannelInitializer;
import io.netty.channel.EventLoopGroup;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.SocketChannel;
import io.netty.channel.socket.nio.NioSocketChannel;
import io.netty.handler.codec.http.HttpClientCodec;
import io.netty.handler.codec.http.HttpObjectAggregator;

public class NettyHttpConnectClient {
    private EventLoopGroup group;
    private Channel channel;
    private Serializer serializer;
    private String address;
    private String host;

    public void init(String address, Serializer serializer, XxlRpcInvokerFactory xxlRpcInvokerFactory) {
        this.address = address;
        this.host = address.split("//")[1].split("/")[0];
        this.serializer = serializer;
        this.group = new NioEventLoopGroup();
        Bootstrap b = new Bootstrap();
        b.group(group)
         .channel(NioSocketChannel.class)
         .handler(new ChannelInitializer<SocketChannel>() {
             @Override
             protected void initChannel(SocketChannel ch) throws Exception {
                 ch.pipeline().addLast(new HttpClientCodec());
                 ch.pipeline().addLast(new HttpObjectAggregator(1048576));
                 ch.pipeline().addLast(new NettyHttpConnectHandler(serializer, xxlRpcInvokerFactory));
             }
         });
        ChannelFuture f = b.connect(host, 80).syncUninterruptibly();
        this.channel = f.channel();
    }

    public boolean isValidate() {
        return channel != null && channel.isActive();
    }

    public void close() {
        if (channel != null) {
            channel.close();
        }
        if (group != null) {
            group.shutdownGracefully();
        }
    }

    public void send(XxlRpcRequest xxlRpcRequest) {
        if (isValidate()) {
            byte[] serializedRequest = serializer.serialize(xxlRpcRequest);
            // Assuming there's a way to send bytes through the channel
            channel.writeAndFlush(serializedRequest);
        } else {
            throw new RuntimeException("Connection is not valid");
        }
    }
}
