import io.micrometer.core.instrument.Counter;
import io.micrometer.core.instrument.Gauge;
import io.micrometer.core.instrument.MeterRegistry;
import io.netty.bootstrap.Bootstrap;
import io.netty.channel.Channel;
import io.netty.channel.EventLoopGroup;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.nio.NioSocketChannel;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.net.InetSocketAddress;
import java.util.concurrent.ConcurrentHashMap;
import java.util.concurrent.atomic.AtomicDouble;

public class HttpEventChannelManager {
    private static final Logger LOG = LoggerFactory.getLogger(HttpEventChannelManager.class);
    private final Counter connectionSuccess;
    private final Counter connectionFailure;
    private final AtomicDouble liveConnections;
    private final AtomicDouble nettyChannelBufferSize;
    private final int lowWriteBufferWatermark;
    private final int highWriteBufferWatermark;
    private final EventLoopGroup eventLoopGroup;
    private final EventLoopGroup encoderEventLoopGroup;
    private final Bootstrap bootstrap;
    private final ConcurrentHashMap<String, Channel> channels;

    public HttpEventChannelManager(MeterRegistry registry, MrePublishConfiguration config) {
        this.connectionSuccess = Counter.builder("netty.connections.success").register(registry);
        this.connectionFailure = Counter.builder("netty.connections.failure").register(registry);
        this.liveConnections = Gauge.builder("netty.connections.active", channels::size).register(registry);
        this.nettyChannelBufferSize = Gauge.builder("netty.channel.buffer.size", () -> {
            double totalSize = 0;
            for (Channel channel : channels.values()) {
                totalSize += channel.config().getWriteBufferHighWaterMark();
            }
            return totalSize;
        }).register(registry);
        this.lowWriteBufferWatermark = config.getLowWriteBufferWatermark();
        this.highWriteBufferWatermark = config.getHighWriteBufferWatermark();
        this.eventLoopGroup = new NioEventLoopGroup(config.getEventLoopThreads());
        this.encoderEventLoopGroup = config.isUseEncoderEventLoop() ? new NioEventLoopGroup(config.getEncoderEventLoopThreads()) : null;
        this.bootstrap = new Bootstrap()
                .group(eventLoopGroup)
                .channel(NioSocketChannel.class)
                .option(io.netty.channel.ChannelOption.WRITE_BUFFER_WATER_MARK,
                        new io.netty.channel.WriteBufferWaterMark(lowWriteBufferWatermark, highWriteBufferWatermark));
        this.channels = new ConcurrentHashMap<>();
    }

    public Channel findOrCreate(InetSocketAddress address) {
        String key = getHostPortString(address);
        return channels.computeIfAbsent(key, k -> {
            Channel channel = bootstrap.connect(address).addListener(future -> {
                if (future.isSuccess()) {
                    connectionSuccess.increment();
                    liveConnections.incrementAndGet();
                } else {
                    connectionFailure.increment();
                }
            }).channel();
            return channel;
        });
    }

    public Channel find(InetSocketAddress address) {
        return channels.get(getHostPortString(address));
    }

    public void close(InetSocketAddress address) {
        String key = getHostPortString(address);
        Channel channel = channels.remove(key);
        if (channel != null) {
            channel.close().addListener(future -> {
                if (!future.isSuccess()) {
                    LOG.error("Failed to close channel to {}", key, future.cause());
                } else {
                    liveConnections.decrementAndGet();
                }
            });
        }
    }

    private String getHostPortString(InetSocketAddress address) {
        return address.getHostString() + ":" + address.getPort();
    }

    public void shutdown() {
        for (Channel channel : channels.values()) {
            channel.close();
        }
        eventLoopGroup.shutdownGracefully();
        if (encoderEventLoopGroup != null) {
            encoderEventLoopGroup.shutdownGracefully();
        }
    }
}
