import io.netty.bootstrap.Bootstrap;
import io.netty.channel.*;
import io.netty.channel.group.ChannelGroup;
import io.netty.channel.group.DefaultChannelGroup;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.SocketChannel;
import io.netty.channel.socket.nio.NioSocketChannel;
import io.netty.handler.ssl.SslContext;
import io.netty.handler.ssl.SslContextBuilder;
import io.netty.handler.ssl.util.InsecureTrustManagerFactory;

import javax.net.ssl.SSLException;
import java.util.concurrent.TimeUnit;

public abstract class TrackerClient {

    protected boolean secure;
    protected long interval;
    protected Bootstrap bootstrap;
    protected int port;
    protected String address;
    protected String[] devices;
    protected ChannelGroup channelGroup = new DefaultChannelGroup(GlobalEventExecutor.INSTANCE);

    public TrackerClient(Config config, String protocol) {
        this.secure = config.isSecure();
        this.interval = config.getReconnectionInterval();
        this.port = config.getPort();
        this.address = config.getAddress();
        this.devices = config.getDevices();

        EventLoopGroup group = new NioEventLoopGroup();
        bootstrap = new Bootstrap()
                .group(group)
                .channel(NioSocketChannel.class)
                .option(ChannelOption.SO_KEEPALIVE, true)
                .handler(new ChannelInitializer<SocketChannel>() {
                    @Override
                    protected void initChannel(SocketChannel ch) throws Exception {
                        ChannelPipeline pipeline = ch.pipeline();
                        if (secure) {
                            SslContext sslCtx = SslContextBuilder.forClient()
                                    .trustManager(InsecureTrustManagerFactory.INSTANCE)
                                    .build();
                            pipeline.addLast(sslCtx.newHandler(ch.alloc()));
                        }
                        addProtocolHandlers(pipeline, config);
                    }
                });
    }

    protected abstract void addProtocolHandlers(ChannelPipeline pipeline, Config config);

    public void start() {
        bootstrap.connect(address, port).addListener(future -> {
            if (future.isSuccess()) {
                System.out.println("Connected to " + address + ":" + port);
            } else {
                System.err.println("Failed to connect to " + address + ":" + port);
                if (interval > 0) {
                    scheduleReconnect();
                }
            }
        });
    }

    private void scheduleReconnect() {
        bootstrap.config().group().schedule(() -> {
            if (!bootstrap.config().group().isShutdown()) {
                start();
            }
        }, interval, TimeUnit.SECONDS);
    }

    public void stop() {
        channelGroup.close();
        bootstrap.config().group().shutdownGracefully();
    }

    public boolean isDatagram() {
        return false;
    }

    public boolean isSecure() {
        return secure;
    }

    public String[] getDevices() {
        return devices;
    }

    public ChannelGroup getChannelGroup() {
        return channelGroup;
    }
}
