        initializer = new Http2ClientInitializer();
        bootstrap = new Bootstrap();
        configureBootstrap(host, port);
    }

    private void configureBootstrap(String host, int port) {
        bootstrap.group(workerGroup)
                .channel(NioSocketChannel.class)
                .handler(new LoggingHandler(LogLevel.INFO))
                .handler(initializer)
                .option(ChannelOption.SO_KEEPALIVE, true)
                .remoteAddress(host, port);
    }

    public void connect() {
        try {
            channel = bootstrap.connect().sync().channel();
            System.out.println("Connected to " + channel.remoteAddress());
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
            System.err.println("Connection interrupted");
        }
    }

    public void sendRequest(URI uri) {
        if (channel != null && channel.isActive()) {
            String scheme = uri.getScheme() == null ? "https" : uri.getScheme();
            String authority = uri.getRawAuthority() == null ? "localhost:443" : uri.getRawAuthority();
            String path = uri.getRawPath() == null ? "/" : uri.getRawPath();
            String query = uri.getRawQuery() == null ? "" : "?" + uri.getRawQuery();

            DefaultFullHttpRequest request = new DefaultFullHttpRequest(
                    HttpVersion.HTTP_1_1,
                    HttpMethod.GET,
                    path + query);

            request.headers().set(HttpHeaderNames.HOST, authority);
            request.headers().set(HttpHeaderNames.USER_AGENT, "Netty");
            request.headers().set(HttpHeaderNames.ACCEPT_ENCODING, "gzip, deflate");
            request.headers().setInt(HttpConversionUtil.ExtensionHeaderNames.STREAM_ID.text(), 3);

            channel.writeAndFlush(request);
        } else {
            System.err.println("Channel is not active");
        }
    }

    public void disconnect() {
        if (channel != null) {
            channel.closeFuture().syncUninterruptibly();
            workerGroup.shutdownGracefully();
            System.out.println("Disconnected from " + channel.remoteAddress());
        }
    }

    static class Http2ClientInitializer extends ChannelInitializer<SocketChannel> {
        @Override
        protected void initChannel(SocketChannel ch) throws Exception {
            ChannelPipeline p = ch.pipeline();
            p.addLast(new Http2MultiplexCodecBuilder(true, new UpgradeRequestHandler()).build());
            p.addLast(new Http2SettingsHandler(false));
            p.addLast(new UserEventLogger());
        }
    }

    static class Http2SettingsHandler extends SimpleChannelInboundHandler<Http2Settings> {
        private final boolean isServer;

        Http2SettingsHandler(boolean isServer) {
            this.isServer = isServer;
        }

        @Override
        protected void channelRead0(ChannelHandlerContext ctx, Http2Settings msg) throws Exception {
            System.out.println("Received HTTP/2 settings: " + msg);
            ctx.fireChannelRead(msg);
        }
    }

    static class UpgradeRequestHandler extends ChannelInboundHandlerAdapter {
        @Override
        public void userEventTriggered(ChannelHandlerContext ctx, Object evt) throws Exception {
            if (evt instanceof SslHandshakeCompletionEvent) {
                SslHandshakeCompletionEvent event = (SslHandshakeCompletionEvent) evt;
                if (event.isSuccess()) {
                    ctx.pipeline().addLast(new Http2ClientUpgradeCodec(new Http2SettingsHandler(false)));
                } else {
                    System.err.println("SSL handshake failed: " + event.cause());
                }
            }
            ctx.fireUserEventTriggered(evt);
        }
    }

    static class UserEventLogger extends ChannelInboundHandlerAdapter {
        @Override
        public void userEventTriggered(ChannelHandlerContext ctx, Object evt) throws Exception {
            System.out.println("User event triggered: " + evt);
            ctx.fireUserEventTriggered(evt);
        }
    }
}
