import io.netty.handler.proxy.Socks5ProxyHandler;

import java.net.URI;
import java.util.concurrent.TimeUnit;

public class NettyTcpClient {
    private static final Logger LOG = Logger.getLogger(NettyTcpClient.class);
    private final URI uri;
    private Channel webSocketChannel;
    private final NioEventLoopGroup eventLoopGroup;
    private final boolean compressedMessages;
    private final boolean acceptAllCertificates;
    private final boolean enableLoggingHandler;
    private final LogLevel loggingHandlerLevel;
    private final String socksProxyHost;
    private final Integer socksProxyPort;

    public NettyTcpClient(URI uri, boolean compressedMessages, boolean acceptAllCertificates, boolean enableLoggingHandler, LogLevel loggingHandlerLevel, String socksProxyHost, Integer socksProxyPort) {
        this.uri = uri;
        this.compressedMessages = compressedMessages;
        this.acceptAllCertificates = acceptAllCertificates;
        this.enableLoggingHandler = enableLoggingHandler;
        this.loggingHandlerLevel = loggingHandlerLevel;
        this.socksProxyHost = socksProxyHost;
        this.socksProxyPort = socksProxyPort;
        this.eventLoopGroup = new NioEventLoopGroup();
    }

    public void connect() throws Exception {
        final SslContext sslCtx;
        if (acceptAllCertificates) {
            sslCtx = SslContextBuilder.forClient().trustManager(InsecureTrustManagerFactory.INSTANCE).build();
        } else {
            sslCtx = SslContextBuilder.forClient().build();
        }

        Bootstrap b = new Bootstrap();
        b.group(eventLoopGroup)
         .channel(NioSocketChannel.class)
         .option(ChannelOption.CONNECT_TIMEOUT_MILLIS, 10000)
         .handler(new ChannelInitializer<SocketChannel>() {
             @Override
             protected void initChannel(SocketChannel ch) throws Exception {
                 ChannelPipeline pipeline = ch.pipeline();

                 if (enableLoggingHandler) {
                     pipeline.addLast(new LoggingHandler(loggingHandlerLevel));
                 }

                 if (socksProxyHost != null && socksProxyPort != null) {
                     pipeline.addLast(new Socks5ProxyHandler(socksProxyHost, socksProxyPort));
                 }

                 pipeline.addLast(sslCtx.newHandler(ch.alloc(), uri.getHost(), uri.getPort()));
                 pipeline.addLast(new HttpClientCodec());
                 pipeline.addLast(new HttpObjectAggregator(8192));
                 pipeline.addLast(getWebSocketClientExtensionHandler());
                 pipeline.addLast(new WebSocketClientProtocolHandler(new WebSocketClientHandshakerFactory(uri, WebSocketVersion.V13, null, true, new DefaultHttpHeaders())));
                 pipeline.addLast(getWebSocketClientHandler(null, null));
             }
         });

        ChannelFuture f = b.connect(uri.getHost(), uri.getPort()).sync();
        webSocketChannel = f.channel();
    }

    public void disconnect() {
        if (webSocketChannel != null && webSocketChannel.isActive()) {
            webSocketChannel.close();
        }
        eventLoopGroup.shutdownGracefully();
    }

    public WebSocketClientExtensionHandler getWebSocketClientExtensionHandler() {
        if (compressedMessages) {
            return new WebSocketClientCompressionHandler();
        }
        return WebSocketClientExtensionHandler.EmptyHandler.INSTANCE;
    }

    public WebSocketClientHandler getWebSocketClientHandler(WebSocketClientHandshaker handshaker, WebSocketClientHandler.WebSocketMessageHandler messageHandler) {
        return new WebSocketClientHandler(handshaker, messageHandler);
    }
}
