import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import java.nio.file.attribute.FileAttribute;
import java.nio.file.attribute.PosixFilePermissions;
import java.util.Set;

public class CachedOpenFile {
    private final FileChannel fc;
    private final ReadWriteLock lock = new ReentrantReadWriteLock();
    private final long capacity;

    public CachedOpenFile(Path file, long capacity) throws IOException {
        this.capacity = capacity;
        Files.createDirectories(file.getParent());
        Set<PosixFilePermission> perms = PosixFilePermissions.fromString("rw-r--r--");
        FileAttribute<Set<PosixFilePermission>> attr = PosixFilePermissions.asFileAttribute(perms);
        this.fc = FileChannel.open(file, StandardOpenOption.CREATE, StandardOpenOption.READ, StandardOpenOption.WRITE, attr);
    }

    public int readBlock(ByteBuffer buffer, long offset) throws IOException {
        ensureOpen();
        lock.readLock().lock();
        try {
            return fc.read(buffer, offset);
        } finally {
            lock.readLock().unlock();
        }
    }

    public int writeBlock(ByteBuffer buffer, long offset) throws IOException {
        ensureOpen();
        lock.writeLock().lock();
        try {
            return fc.write(buffer, offset);
        } finally {
            lock.writeLock().unlock();
        }
    }

    public int writeBlock(ByteBufferView buffer, long offset) throws IOException {
        ensureOpen();
        lock.writeLock().lock();
        try {
            return fc.write(buffer, offset);
        } finally {
            lock.writeLock().unlock();
        }
    }

    public long capacity() {
        return capacity;
    }

    public long size() throws IOException {
        ensureOpen();
        lock.readLock().lock();
        try {
            return fc.size();
        } finally {
            lock.readLock().unlock();
        }
    }

    public void flush() throws IOException {
        ensureOpen();
        lock.writeLock().lock();
        try {
            fc.force(true);
        } finally {
            lock.writeLock().unlock();
        }
    }

    public void lockForIoOperation() {
        lock.readLock().lock();
    }

    public void unlockForIoOperation() {
        lock.readLock().unlock();
    }

    public void close() throws IOException {
        if (fc.isOpen()) {
            fc.close();
        }
    }

    private void ensureOpen() throws IOException {
        if (!fc.isOpen()) {
            throw new IOException("FileChannel is closed");
        }
    }
}
