import io.netty.handler.ssl.SslContext;
import io.netty.handler.ssl.SslContextBuilder;
import io.netty.handler.ssl.util.InsecureTrustManagerFactory;
import io.netty.handler.timeout.ReadTimeoutException;
import io.netty.handler.timeout.ReadTimeoutHandler;

import javax.net.ssl.SSLParameters;
import java.util.concurrent.TimeUnit;

public class NettyTcpClient {

    private Bootstrap bootstrap;
    private EventLoopGroup eventLoopGroup;
    private int connectTimeout;
    private int readTimeout;
    private String threadPrefix;

    public NettyTcpClient(int connectTimeout, int readTimeout, String threadPrefix) {
        this.connectTimeout = connectTimeout;
        this.readTimeout = readTimeout;
        this.threadPrefix = threadPrefix;
    }

    public void init(DockerClientConfig dockerClientConfig) {
        eventLoopGroup = new NioEventLoopGroup(0, r -> {
            Thread t = new Thread(r);
            t.setName(threadPrefix + "-client");
            return t;
        });
        bootstrap = new Bootstrap();
        bootstrap.group(eventLoopGroup)
                .channel(NioSocketChannel.class)
                .option(io.netty.channel.ChannelOption.CONNECT_TIMEOUT_MILLIS, connectTimeout);
    }

    public DuplexChannel connect(DockerClientConfig dockerClientConfig) throws Exception {
        SslHandler sslHandler = initSsl(dockerClientConfig);
        bootstrap.handler(new ChannelInitializer<SocketChannel>() {
            @Override
            protected void initChannel(SocketChannel ch) throws Exception {
                ChannelPipeline pipeline = ch.pipeline();
                if (sslHandler != null) {
                    pipeline.addLast(sslHandler);
                }
                pipeline.addLast(new StringDecoder());
                pipeline.addLast(new StringEncoder());
                pipeline.addLast(new ReadTimeoutHandler(readTimeout, TimeUnit.MILLISECONDS));
                pipeline.addLast(new ChannelInboundHandlerAdapter() {
                    @Override
                    public void exceptionCaught(ChannelHandlerContext ctx, Throwable cause) throws Exception {
                        if (cause instanceof ReadTimeoutException) {
                            System.err.println("Read timeout occurred");
                        } else {
                            cause.printStackTrace();
                        }
                        ctx.close();
                    }
                });
            }
        });

        ChannelFuture future = bootstrap.connect(dockerClientConfig.getHost(), dockerClientConfig.getPort()).sync();
        return new DuplexChannel(future.channel());
    }

    private SslHandler initSsl(DockerClientConfig dockerClientConfig) throws Exception {
        SslContext sslContext = SslContextBuilder.forClient()
                .trustManager(InsecureTrustManagerFactory.INSTANCE)
                .build();
        SSLParameters sslParameters = sslContext.newEngine().getSSLParameters();
        enableHostNameVerification(sslParameters);
        return sslContext.newHandler(eventLoopGroup.next().asExecutor(), dockerClientConfig.getHost(), dockerClientConfig.getPort(), sslParameters);
    }

    private SSLParameters enableHostNameVerification(SSLParameters sslParameters) {
        sslParameters.setEndpointIdentificationAlgorithm("HTTPS");
        return sslParameters;
    }

    public void close() {
        if (eventLoopGroup != null) {
            eventLoopGroup.shutdownGracefully();
        }
    }

    private <T> T configure(T channel) {
        // Additional configuration logic can be added here
        return channel;
    }

    private static class ReadTimeoutHandler extends io.netty.handler.timeout.ReadTimeoutHandler {
        public ReadTimeoutHandler(int timeout, TimeUnit unit) {
            super(timeout, unit);
        }
    }
}
