import io.netty.channel.EventLoopGroup;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.SocketChannel;
import io.netty.channel.socket.nio.NioServerSocketChannel;
import io.netty.handler.codec.string.StringDecoder;
import io.netty.handler.codec.string.StringEncoder;
import io.netty.handler.ssl.SslContext;
import io.netty.handler.ssl.SslContextBuilder;
import io.netty.handler.ssl.util.SelfSignedCertificate;

public class NettyServer {
    private Blade blade;
    private Environment environment;
    private EventLoopGroup bossGroup;
    private EventLoopGroup workerGroup;
    private Channel channel;
    private static final int DEFAULT_ACCEPT_THREAD_COUNT = 1;
    private static final int DEFAULT_IO_THREAD_COUNT = Runtime.getRuntime().availableProcessors() * 2;

    public NettyServer(Blade blade, Environment environment) {
        this.blade = blade;
        this.environment = environment;
    }

    public void startServer() throws Exception {
        int acceptThreadCount = environment.getProperty("server.acceptThreads", Integer.class, DEFAULT_ACCEPT_THREAD_COUNT);
        int ioThreadCount = environment.getProperty("server.ioThreads", Integer.class, DEFAULT_IO_THREAD_COUNT);

        bossGroup = new NioEventLoopGroup(acceptThreadCount);
        workerGroup = new NioEventLoopGroup(ioThreadCount);

        ServerBootstrap bootstrap = new ServerBootstrap();
        bootstrap.group(bossGroup, workerGroup)
                 .channel(NioServerSocketChannel.class)
                 .childHandler(new ChannelInitializer<SocketChannel>() {
                     @Override
                     protected void initChannel(SocketChannel ch) throws Exception {
                         ch.pipeline().addLast(new StringDecoder(), new StringEncoder());
                         // Add more handlers as needed
                     }
                 });

        if (environment.getProperty("server.ssl.enabled", Boolean.class, false)) {
            SelfSignedCertificate ssc = new SelfSignedCertificate();
            SslContext sslCtx = SslContextBuilder.forServer(ssc.certificate(), ssc.privateKey()).build();
            bootstrap.childHandler(new ChannelInitializer<SocketChannel>() {
                @Override
                protected void initChannel(SocketChannel ch) throws Exception {
                    ch.pipeline().addLast(sslCtx.newHandler(ch.alloc()));
                    ch.pipeline().addLast(new StringDecoder(), new StringEncoder());
                    // Add more handlers as needed
                }
            });
        }

        int port = environment.getProperty("server.port", Integer.class, 8080);
        ChannelFuture future = bootstrap.bind(port).sync();
        channel = future.channel();
        System.out.println("Server started on port " + port);
    }

    public void stop() {
        if (channel != null) {
            channel.close();
        }
        workerGroup.shutdownGracefully();
        bossGroup.shutdownGracefully();
        System.out.println("Server stopped");
    }
}
