import io.netty.bootstrap.ServerBootstrap;
import io.netty.channel.*;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.SocketChannel;
import io.netty.channel.socket.nio.NioServerSocketChannel;
import io.netty.handler.codec.http.*;

import java.net.URL;

public class Netty4HttpServer extends AbstractServer implements StatisticCallback {
    private HttpMessageHandler httpMessageHandler;
    private URL url;
    private Channel channel;
    private EventLoopGroup bossGroup;
    private EventLoopGroup workerGroup;
    private StandardThreadExecutor standardThreadExecutor;

    public Netty4HttpServer(HttpMessageHandler httpMessageHandler, URL url) {
        this.httpMessageHandler = httpMessageHandler;
        this.url = url;
        this.standardThreadExecutor = new StandardThreadExecutor();
    }

    public boolean open() {
        bossGroup = new NioEventLoopGroup(1);
        workerGroup = new NioEventLoopGroup(standardThreadExecutor.getThreadPoolSize());

        try {
            ServerBootstrap b = new ServerBootstrap();
            b.group(bossGroup, workerGroup)
             .channel(NioServerSocketChannel.class)
             .childHandler(new ChannelInitializer<SocketChannel>() {
                 @Override
                 protected void initChannel(SocketChannel ch) throws Exception {
                     ChannelPipeline p = ch.pipeline();
                     p.addLast(new HttpServerCodec());
                     p.addLast(new HttpObjectAggregator(65536));
                     p.addLast(new SimpleChannelInboundHandler<FullHttpRequest>() {
                         @Override
                         protected void channelRead0(ChannelHandlerContext ctx, FullHttpRequest req) throws Exception {
                             processHttpRequest(ctx, req);
                         }
                     });
                 }
             })
             .option(ChannelOption.SO_BACKLOG, 128)
             .childOption(ChannelOption.SO_KEEPALIVE, true);

            channel = b.bind(url.getPort()).sync().channel();
            return true;
        } catch (Exception e) {
            e.printStackTrace();
            return false;
        }
    }

    public void close(int timeout) {
        try {
            if (channel != null) {
                channel.closeFuture().sync();
            }
            bossGroup.shutdownGracefully().awaitUninterruptibly(timeout);
            workerGroup.shutdownGracefully().awaitUninterruptibly(timeout);
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    public void processHttpRequest(ChannelHandlerContext ctx, FullHttpRequest httpRequest) {
        FullHttpResponse response = httpMessageHandler.handleRequest(httpRequest);
        sendResponse(ctx, response);
    }

    public void sendResponse(ChannelHandlerContext ctx, FullHttpResponse httpResponse) {
        ctx.writeAndFlush(httpResponse).addListener(ChannelFutureListener.CLOSE);
    }

    public URL getUrl() {
        return url;
    }
}
