    private EventLoopGroup bossGroup;
    private EventLoopGroup workerGroup;
    private ChannelFuture channelFuture;

    public void start(XxlRpcProviderFactory xxlRpcProviderFactory) {
        int port = xxlRpcProviderFactory.getPort();
        int bossThreads = xxlRpcProviderFactory.getBossThreads();
        int workerThreads = xxlRpcProviderFactory.getWorkerThreads();

        bossGroup = new NioEventLoopGroup(bossThreads);
        workerGroup = new NioEventLoopGroup(workerThreads);

        ServerBootstrap bootstrap = new ServerBootstrap();
        bootstrap.group(bossGroup, workerGroup)
                 .channel(NioServerSocketChannel.class)
                 .childHandler(new ChannelInitializer<SocketChannel>() {
                     @Override
                     protected void initChannel(SocketChannel ch) throws Exception {
                         ch.pipeline().addLast(new HttpServerCodec());
                         ch.pipeline().addLast(new HttpObjectAggregator(65536));
                         ch.pipeline().addLast(new IdleStateHandler(0, 0, 60));
                         ch.pipeline().addLast(new XxlRpcNettyServerHandler());
                     }
                 })
                 .option(ChannelOption.SO_BACKLOG, 128)
                 .childOption(ChannelOption.SO_KEEPALIVE, true);

        try {
            channelFuture = bootstrap.bind(port).sync();
            logger.info("Netty HTTP server started on port {}", port);
        } catch (InterruptedException e) {
            logger.error("Failed to start Netty HTTP server", e);
        }

        thread = new Thread(() -> {
            try {
                channelFuture.channel().closeFuture().sync();
            } catch (InterruptedException e) {
                logger.error("Netty HTTP server interrupted", e);
            } finally {
                stop();
            }
        });
        thread.start();
    }

    public void stop() {
        if (channelFuture != null && channelFuture.channel().isOpen()) {
            channelFuture.channel().close();
        }

        if (bossGroup != null) {
            bossGroup.shutdownGracefully();
        }

        if (workerGroup != null) {
            workerGroup.shutdownGracefully();
        }

        if (thread != null) {
            thread.interrupt();
        }

        logger.info("Netty HTTP server stopped");
    }
}
