import io.vertx.core.AsyncResult;
import io.vertx.core.ContextInternal;
import io.vertx.core.Future;
import io.vertx.core.Handler;
import io.vertx.core.Promise;
import io.vertx.core.buffer.Buffer;
import io.vertx.core.file.OpenOptions;
import io.vertx.core.impl.VertxInternal;

import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.AsynchronousFileChannel;
import java.nio.channels.CompletionHandler;
import java.nio.file.Path;
import java.nio.file.Paths;
import java.nio.file.StandardOpenOption;
import java.util.Set;

public class AsyncFileImpl implements AsyncFile {

  private final VertxInternal vertx;
  private AsynchronousFileChannel ch;
  private final ContextInternal context;
  private boolean closed = false;
  private long writePos = 0;
  private int readBufferSize = 8192;
  private Handler<Buffer> handler;
  private Handler<Void> endHandler;
  private long readPos = 0;
  private long readLength = Long.MAX_VALUE;

  public AsyncFileImpl(VertxInternal vertx, String path, OpenOptions options, ContextInternal context) {
    this.vertx = vertx;
    this.context = context;
    try {
      Path filePath = Paths.get(path);
      Set<? extends StandardOpenOption> openOptions = options.toSet();
      ch = AsynchronousFileChannel.open(filePath, openOptions, vertx.getEventLoopGroup());
    } catch (IOException e) {
      throw new RuntimeException("Failed to open file", e);
    }
  }

  @Override
  public Future<Void> close() {
    Promise<Void> promise = Promise.promise();
    closeInternal(promise);
    return promise.future();
  }

  @Override
  public Future<Buffer> read(Buffer buffer, int offset, long position, int length) {
    Promise<Buffer> promise = Promise.promise();
    doRead(buffer, offset, ByteBuffer.allocate(length), position, promise);
    return promise.future();
  }

  @Override
  public Future<Void> write(Buffer buffer, long position) {
    Promise<Void> promise = Promise.promise();
    doWrite(buffer, position, promise);
    return promise.future();
  }

  private void doWrite(Buffer buffer, long position, Handler<AsyncResult<Void>> handler) {
    ByteBuffer[] buffers = buffer.getBytes().toByteBuffers();
    doWrite(buffers, position, handler);
  }

  private void doWrite(ByteBuffer[] buffers, long position, Handler<AsyncResult<Void>> handler) {
    for (ByteBuffer buff : buffers) {
      doWrite(buff, position, buff.remaining(), handler);
      position += buff.remaining();
    }
  }

  private void doWrite(ByteBuffer buff, long position, long toWrite, Handler<AsyncResult<Void>> handler) {
    writeInternal(buff, position, handler);
  }

  private void writeInternal(ByteBuffer buff, long position, Handler<AsyncResult<Void>> handler) {
    ch.write(buff, position, null, new CompletionHandler<Integer, Void>() {
      @Override
      public void completed(Integer result, Void attachment) {
        if (result < 0) {
          handler.handle(Future.failedFuture(new IOException("End of file reached")));
        } else {
          handler.handle(Future.succeededFuture());
        }
      }

      @Override
      public void failed(Throwable exc, Void attachment) {
        handler.handle(Future.failedFuture(exc));
      }
    });
  }

  private void doRead(Buffer writeBuff, int offset, ByteBuffer buff, long position, Promise<Buffer> promise) {
    ch.read(buff, position, null, new CompletionHandler<Integer, Void>() {
      @Override
      public void completed(Integer result, Void attachment) {
        if (result == -1) {
          promise.complete(writeBuff);
        } else {
          buff.flip();
          writeBuff.appendBytes(buff.array(), buff.position(), buff.limit());
          doRead(writeBuff, offset + result, buff, position + result, promise);
        }
      }

      @Override
      public void failed(Throwable exc, Void attachment) {
        promise.fail(exc);
      }
    });
  }

  private void check() {
    if (closed) {
      throw new IllegalStateException("File is closed");
    }
  }

  private void closeInternal(Promise<Void> handler) {
    if (!closed) {
      closed = true;
      try {
        ch.close();
        handler.complete();
      } catch (IOException e) {
        handler.fail(e);
      }
    } else {
      handler.complete();
    }
  }
}
