import io.netty.bootstrap.ServerBootstrap;
import io.netty.channel.ChannelFuture;
import io.netty.channel.ChannelInitializer;
import io.netty.channel.ChannelPipeline;
import io.netty.channel.EventLoopGroup;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.SocketChannel;
import io.netty.channel.socket.nio.NioServerSocketChannel;
import io.netty.handler.codec.http2.Http2ServerUpgradeHandler;
import io.netty.handler.logging.LogLevel;
import io.netty.handler.logging.LoggingHandler;
import io.netty.handler.ssl.SslContext;
import io.netty.handler.ssl.SslContextBuilder;
import io.netty.handler.ssl.util.SelfSignedCertificate;

import java.security.cert.CertificateException;

public abstract class AbstractHttp2ServerTransport {

    protected static final Logger LOGGER = Logger.getLogger(AbstractHttp2ServerTransport.class.getName());
    private ServerBootstrap serverBootstrap;
    private EventLoopGroup bossGroup;
    private EventLoopGroup workerGroup;
    protected ServerTransportConfig transportConfig;

    public AbstractHttp2ServerTransport(ServerTransportConfig transportConfig) {
        this.transportConfig = transportConfig;
    }

    public boolean start() {
        bossGroup = new NioEventLoopGroup(1);
        workerGroup = new NioEventLoopGroup();
        serverBootstrap = new ServerBootstrap();

        try {
            SslContext sslCtx = buildSslContext();
            serverBootstrap.group(bossGroup, workerGroup)
                    .channel(NioServerSocketChannel.class)
                    .handler(new LoggingHandler(LogLevel.INFO))
                    .childHandler(new ChannelInitializer<SocketChannel>() {
                        @Override
                        protected void initChannel(SocketChannel ch) throws Exception {
                            ChannelPipeline pipeline = ch.pipeline();
                            pipeline.addLast(sslCtx.newHandler(ch.alloc()));
                            pipeline.addLast(new Http2ServerUpgradeHandler(
                                    new InsecureTrustManagerFactory(),
                                    protocol -> new Http2FrameCodecBuilder(true).build(),
                                    65535));
                            addCustomHandlers(pipeline);
                        }
                    });

            ChannelFuture future = serverBootstrap.bind(transportConfig.getHost(), transportConfig.getPort()).sync();
            future.channel().closeFuture().sync();
            return true;
        } catch (InterruptedException | CertificateException e) {
            LOGGER.severe("Failed to start HTTP/2 server: " + e.getMessage());
            return false;
        } finally {
            stop();
        }
    }

    public void stop() {
        try {
            if (bossGroup != null) bossGroup.shutdownGracefully();
            if (workerGroup != null) workerGroup.shutdownGracefully();
        } catch (Exception e) {
            LOGGER.severe("Error shutting down server: " + e.getMessage());
        }
    }

    protected abstract void addCustomHandlers(ChannelPipeline pipeline);

    private SslContext buildSslContext() throws CertificateException {
        SelfSignedCertificate ssc = new SelfSignedCertificate();
        return SslContextBuilder.forServer(ssc.certificate(), ssc.privateKey()).build();
    }
}
