import io.netty.handler.codec.http.HttpRequestDecoder;
import io.netty.handler.codec.http.HttpResponseEncoder;
import io.netty.handler.logging.LogLevel;
import io.netty.handler.logging.LoggingHandler;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

public class LoFileServer {
    private final int port;
    private Channel channel;
    private static final Logger logger = LoggerFactory.getLogger(LoFileServer.class);

    public LoFileServer(int port) {
        this.port = port;
    }

    public void start() throws InterruptedException {
        EventLoopGroup bossGroup = new NioEventLoopGroup(1);
        EventLoopGroup workerGroup = new NioEventLoopGroup();
        try {
            ServerBootstrap b = new ServerBootstrap();
            b.group(bossGroup, workerGroup)
             .channel(NioServerSocketChannel.class)
             .option(ChannelOption.SO_BACKLOG, 100)
             .handler(new LoggingHandler(LogLevel.INFO))
             .childHandler(new ChannelInitializer<SocketChannel>() {
                 @Override
                 protected void initChannel(SocketChannel ch) throws Exception {
                     ch.pipeline().addLast(
                             new HttpResponseEncoder(),
                             new HttpRequestDecoder(),
                             new HttpObjectAggregator(65536),
                             new FileServerHandler()
                     );
                 }
             });

            ChannelFuture f = b.bind(port).sync();
            channel = f.channel();
            logger.info("HTTP file server started on port {}", port);
            f.channel().closeFuture().sync();
        } finally {
            workerGroup.shutdownGracefully();
            bossGroup.shutdownGracefully();
        }
    }

    public void shutdown() {
        if (channel != null && channel.isActive()) {
            channel.close().addListener(future -> {
                if (future.isSuccess()) {
                    logger.info("HTTP file server shutdown successfully");
                } else {
                    logger.error("Failed to shutdown HTTP file server", future.cause());
                }
            });
        }
    }
}
