public class GeyserServer {
    public static final Transport TRANSPORT = compatibleTransport();
    public static final int SHUTDOWN_QUIET_PERIOD_MS = 5000;
    public static final int SHUTDOWN_TIMEOUT_MS = 10000;
    private final GeyserImpl geyser;
    private EventLoopGroup group;
    private EventLoopGroup childGroup;
    private ServerBootstrap bootstrap;
    private EventLoopGroup playerGroup;
    private ChannelFuture[] bootstrapFutures;

    public GeyserServer(GeyserImpl geyser, int threadCount) {
        this.geyser = geyser;
        this.group = new NioEventLoopGroup(threadCount);
        this.childGroup = new NioEventLoopGroup(threadCount);
        this.playerGroup = new NioEventLoopGroup(threadCount);
        this.bootstrap = createBootstrap();
    }

    public CompletableFuture<Void> bind(InetSocketAddress address) {
        CompletableFuture<Void> future = new CompletableFuture<>();
        bootstrapFutures = new ChannelFuture[]{bootstrap.bind(address)};
        bootstrapFutures[0].addListener(f -> {
            if (f.isSuccess()) {
                future.complete(null);
            } else {
                future.completeExceptionally(f.cause());
            }
        });
        return future;
    }

    public void shutdown() {
        for (ChannelFuture future : bootstrapFutures) {
            future.channel().close();
        }
        group.shutdownGracefully(SHUTDOWN_QUIET_PERIOD_MS, SHUTDOWN_TIMEOUT_MS);
        childGroup.shutdownGracefully(SHUTDOWN_QUIET_PERIOD_MS, SHUTDOWN_TIMEOUT_MS);
        playerGroup.shutdownGracefully(SHUTDOWN_QUIET_PERIOD_MS, SHUTDOWN_TIMEOUT_MS);
    }

    private ServerBootstrap createBootstrap() {
        ServerBootstrap b = new ServerBootstrap();
        b.group(group, childGroup)
         .channel(TRANSPORT.channelClass())
         .childHandler(new GeyserChannelInitializer(geyser, playerGroup))
         .option(ChannelOption.SO_BACKLOG, 128)
         .childOption(ChannelOption.SO_KEEPALIVE, true);
        return b;
    }

    private static Transport compatibleTransport() {
        if (isClassAvailable("io.netty.channel.epoll.EpollEventLoopGroup")) {
            return new Transport(EpollDatagramChannel.class, EpollEventLoopGroup::new);
        } else if (isClassAvailable("io.netty.channel.kqueue.KQueueEventLoopGroup")) {
            return new Transport(KQueueDatagramChannel.class, KQueueEventLoopGroup::new);
        } else if (isClassAvailable("io.netty.channel.unix.IovArray") && isClassAvailable("io.netty.channel.unix.IoUringEventLoopGroup")) {
            return new Transport(IoUringDatagramChannel.class, IoUringEventLoopGroup::new);
        } else {
            return new Transport(NioDatagramChannel.class, NioEventLoopGroup::new);
        }
    }

    private static boolean isClassAvailable(String className) {
        try {
            Class.forName(className);
            return true;
        } catch (ClassNotFoundException e) {
            return false;
        }
    }

    public record Transport(Class<?> channelClass, EventLoopGroup.Factory eventLoopGroupFactory) {}
}
