public class Scraper {
    private OkHttpClient client;

    public Scraper() {
        this.client = new OkHttpClient.Builder()
                .build();
    }

    public List<String> scrape(String url, long timeoutMillis) {
        return scrape(url, null, null, timeoutMillis);
    }

    public List<String> scrape(String url, String user, String password, long timeoutMillis) {
        Request.Builder requestBuilder = new Request.Builder().url(url);
        if (user != null && password != null) {
            String credentials = Credentials.basic(user, password);
            requestBuilder.header("Authorization", credentials);
        }
        Request request = requestBuilder.build();

        int retryCount = 0;
        final int maxRetries = 3;
        List<String> responseLines = new ArrayList<>();

        while (retryCount < maxRetries) {
            try (Response response = client.newCall(request).execute()) {
                if (response.isSuccessful()) {
                    responseLines = response.body().string().lines().toList();
                    break;
                } else {
                    retryCount++;
                    Thread.sleep(timeoutMillis);
                }
            } catch (IOException | InterruptedException e) {
                retryCount++;
                try {
                    Thread.sleep(timeoutMillis);
                } catch (InterruptedException ie) {
                    Thread.currentThread().interrupt();
                }
            }
        }

        return responseLines;
    }
}
