    public AcceptSocketService(MessageStoreConfig messageStoreConfig) {
        this.messageStoreConfig = messageStoreConfig;
        this.log = Logger.getLogger(AcceptSocketService.class.getName());
    }

    public void beginAccept() throws IOException {
        serverSocketChannel = ServerSocketChannel.open();
        serverSocketChannel.socket().bind(socketAddressListen);
        serverSocketChannel.configureBlocking(false);

        selector = Selector.open();
        serverSocketChannel.register(selector, SelectionKey.OP_ACCEPT);
    }

    public void shutdown(boolean interrupt) {
        try {
            selector.close();
            serverSocketChannel.close();
        } catch (IOException e) {
            log.severe("Error shutting down server: " + e.getMessage());
        }
        super.shutdown(interrupt);
    }

    @Override
    public void run() {
        while (!isInterrupted()) {
            try {
                selector.select();
                Iterator<SelectionKey> keys = selector.selectedKeys().iterator();
                while (keys.hasNext()) {
                    SelectionKey key = keys.next();
                    if (key.isAcceptable()) {
                        acceptConnection(key);
                    }
                    keys.remove();
                }
            } catch (IOException e) {
                log.severe("Error during selection: " + e.getMessage());
            }
        }
    }

    private void acceptConnection(SelectionKey key) {
        try {
            ServerSocketChannel serverSocketChannel = (ServerSocketChannel) key.channel();
            SocketChannel socketChannel = serverSocketChannel.accept();
            if (socketChannel != null) {
                socketChannel.configureBlocking(false);
                HAConnection connection = createConnection(socketChannel);
                connection.start();
            }
        } catch (IOException e) {
            log.severe("Error accepting connection: " + e.getMessage());
        }
    }

