import java.util.concurrent.BlockingDeque;

public class DaemonHandler {
    private ProcessBuilder processBuilder;
    private Process process;
    private Thread outThread;
    private Thread errThread;
    private OutputProxy errProxy;
    private OutputProxy outProxy;
    private BlockingDeque<Event> deque;
    private boolean stdOut;

    public DaemonHandler(BlockingDeque<Event> deque, String... command) {
        this.deque = deque;
        this.processBuilder = new ProcessBuilder(command);
        this.stdOut = true; // Default to capturing standard output
    }

    public void setStdOutListener(boolean stdOut) {
        this.stdOut = stdOut;
    }

    public Process startDelimited() {
        return startDelimited((byte) '\n');
    }

    public Process startDelimited(byte delimiter) {
        process = startProcess();
        outProxy = new OutputProxy(process.getInputStream(), delimiter);
        errProxy = new OutputProxy(process.getErrorStream(), delimiter);
        startThreads();
        return process;
    }

    public Process startUndelimited() {
        return startUndelimited(1024); // Default buffer size
    }

    public Process startUndelimited(int bufferSize) {
        process = startProcess();
        outProxy = new OutputProxy(process.getInputStream(), bufferSize);
        errProxy = new OutputProxy(process.getErrorStream(), bufferSize);
        startThreads();
        return process;
    }

    private Process startProcess() {
        try {
            return processBuilder.start();
        } catch (IOException e) {
            throw new RuntimeException("Failed to start process", e);
        }
    }

    private void startThreads() {
        outThread = new Thread(outProxy);
        errThread = new Thread(errProxy);
        outThread.start();
        errThread.start();
    }

    public void stop(boolean forcibly) {
        if (process != null) {
            if (forcibly) {
                process.destroyForcibly();
            } else {
                process.destroy();
            }
        }
    }

    public int getExitCode() {
        if (process != null && process.isAlive()) {
            return -1; // Placeholder for PROCESS_STOPPED
        }
        try {
            return process.waitFor();
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
            return -1; // Placeholder for PROCESS_STOPPED
        }
    }

    private class OutputProxy implements Runnable {
        private final InputStream inputStream;
        private final byte delimiter;
        private final int bufferSize;

        public OutputProxy(InputStream inputStream, byte delimiter) {
            this.inputStream = inputStream;
            this.delimiter = delimiter;
            this.bufferSize = 0;
        }

        public OutputProxy(InputStream inputStream, int bufferSize) {
            this.inputStream = inputStream;
            this.delimiter = 0; // No delimiter for undelimited mode
            this.bufferSize = bufferSize;
        }

        @Override
        public void run() {
            try (BufferedReader reader = new BufferedReader(new InputStreamReader(inputStream))) {
                String line;
                if (delimiter != 0) {
                    while ((line = reader.readLine()) != null) {
                        deque.add(new Event(line));
                    }
                } else {
                    char[] buffer = new char[bufferSize];
                    int bytesRead;
                    while ((bytesRead = reader.read(buffer)) != -1) {
                        deque.add(new Event(new String(buffer, 0, bytesRead)));
                    }
                }
            } catch (IOException e) {
                deque.add(new Event(e.getMessage()));
            }
        }
    }

    public static class Event {
        private final String message;

        public Event(String message) {
            this.message = message;
        }

        public String getMessage() {
            return message;
        }
    }
}
