import java.io.*;
import java.nio.charset.Charset;
import java.nio.charset.StandardCharsets;
import java.util.concurrent.ForkJoinPool;
import java.util.concurrent.RecursiveAction;

abstract class AbstractScriptProcessor {

    protected static final ForkJoinPool POOL = new ForkJoinPool();
    protected static final String SH_SHELL = "/bin/sh";
    protected static final String CMD_SHELL = "cmd.exe";

    public ProcessResult executeScript(TaskContext context, String scriptPath) throws IOException, InterruptedException {
        ProcessBuilder pb = new ProcessBuilder(getRunCommand(), scriptPath);
        Process process = pb.start();

        StringBuilder out = new StringBuilder();
        StringBuilder err = new StringBuilder();

        StreamCopyAction outAction = new StreamCopyAction(process.getInputStream(), out, context.getLogger(), getCharset());
        StreamCopyAction errAction = new StreamCopyAction(process.getErrorStream(), err, context.getLogger(), getCharset());

        POOL.invokeAll(outAction, errAction);

        int exitCode = process.waitFor();
        return new ProcessResult(exitCode, out.toString(), err.toString());
    }

    private void copyStream(InputStream is, StringBuilder sb, OmsLogger omsLogger, Charset charset) throws IOException {
        try (BufferedReader reader = new BufferedReader(new InputStreamReader(is, charset))) {
            String line;
            while ((line = reader.readLine()) != null) {
                sb.append(line).append("\n");
                omsLogger.log(line);
            }
        }
    }

    protected abstract String getRunCommand();

    protected Charset getCharset() {
        return StandardCharsets.UTF_8;
    }

    private class StreamCopyAction extends RecursiveAction {
        private final InputStream is;
        private final StringBuilder sb;
        private final OmsLogger omsLogger;
        private final Charset charset;

        public StreamCopyAction(InputStream is, StringBuilder sb, OmsLogger omsLogger, Charset charset) {
            this.is = is;
            this.sb = sb;
            this.omsLogger = omsLogger;
            this.charset = charset;
        }

        @Override
        protected void compute() {
            try {
                copyStream(is, sb, omsLogger, charset);
            } catch (IOException e) {
                omsLogger.log("Error copying stream: " + e.getMessage());
            }
        }
    }
}

class ProcessResult {
    private final int exitCode;
    private final String output;
    private final String error;

    public ProcessResult(int exitCode, String output, String error) {
        this.exitCode = exitCode;
        this.output = output;
        this.error = error;
    }

    public int getExitCode() {
        return exitCode;
    }

    public String getOutput() {
        return output;
    }

    public String getError() {
        return error;
    }
}

interface TaskContext {
    OmsLogger getLogger();
}

interface OmsLogger {
    void log(String message);
}
