public class Command {
    private Process process;
    private OutputStream stdIn;
    private File stdOut;
    private File stdErr;
    private String nextLine;
    private String exit;
    private Charset charset;
    private LocalDateTime startTime;

    private Command(String init, String nextLine, String exit, Charset charset) throws IOException {
        this.nextLine = nextLine;
        this.exit = exit;
        this.charset = charset;
        this.stdOut = File.createTempFile("stdout", ".log");
        this.stdErr = File.createTempFile("stderr", ".log");
        ProcessBuilder pb = new ProcessBuilder(init.split("\\s+"));
        pb.redirectOutput(stdOut);
        pb.redirectError(stdErr);
        process = pb.start();
        stdIn = process.getOutputStream();
        startTime = LocalDateTime.now();
    }

    public static Command of(String init) throws IOException {
        return new Command(init, "\n", "exit", Charset.defaultCharset());
    }

    public static Command of(String init, Charset charset) throws IOException {
        return new Command(init, "\n", "exit", charset);
    }

    public static Command of(String init, String nextLine, String exit, Charset charset) throws IOException {
        return new Command(init, nextLine, exit, charset);
    }

    public Command write(String str) throws IOException {
        stdIn.write(str.getBytes(charset));
        return this;
    }

    public Command line() throws IOException {
        stdIn.write(nextLine.getBytes(charset));
        return this;
    }

    public Command exit() throws IOException {
        stdIn.write(exit.getBytes(charset));
        stdIn.flush();
        return this;
    }

    public Command exec(String str) throws IOException {
        write(str).line();
        return this;
    }

    public CommandResult result() throws IOException {
        return result(Long.MAX_VALUE);
    }

    public CommandResult result(long millis) throws IOException {
        try {
            process.waitFor(millis, TimeUnit.MILLISECONDS);
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        }
        return new CommandResult(process.exitValue(), readFile(stdOut), readFile(stdErr));
    }

    public void close() {
        process.destroyForcibly();
        try {
            stdIn.close();
            stdOut.delete();
            stdErr.delete();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private String readFile(File file) throws IOException {
        StringBuilder content = new StringBuilder();
        try (BufferedReader reader = new BufferedReader(new FileReader(file, charset))) {
            String line;
            while ((line = reader.readLine()) != null) {
                content.append(line).append("\n");
            }
        }
        return content.toString();
    }

    public static class CommandResult {
        private int exitCode;
        private String stdout;
        private String stderr;

        public CommandResult(int exitCode, String stdout, String stderr) {
            this.exitCode = exitCode;
            this.stdout = stdout;
            this.stderr = stderr;
        }

        public int getExitCode() {
            return exitCode;
        }

        public String getStdout() {
            return stdout;
