import java.io.IOException;
import java.io.OutputStream;
import java.nio.ByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.OpenOption;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;

public class FileChannelUtils {

    public static OutputStream newOutputStream(FileChannel channel, boolean forceAtClose) {
        return new OutputStream() {
            private ByteBuffer buffer = ByteBuffer.allocate(8192);

            @Override
            public void write(int b) throws IOException {
                if (!buffer.hasRemaining()) {
                    flush();
                }
                buffer.put((byte) b);
            }

            @Override
            public void write(byte[] b, int off, int len) throws IOException {
                while (len > 0) {
                    if (!buffer.hasRemaining()) {
                        flush();
                    }
                    int toWrite = Math.min(len, buffer.remaining());
                    buffer.put(b, off, toWrite);
                    off += toWrite;
                    len -= toWrite;
                }
            }

            @Override
            public void flush() throws IOException {
                buffer.flip();
                channel.write(buffer);
                buffer.clear();
            }

            @Override
            public void close() throws IOException {
                try {
                    flush();
                    if (forceAtClose) {
                        channel.force(true);
                    }
                } finally {
                    channel.close();
                }
            }
        };
    }

    public static OutputStream newOutputStreamForceAtClose(Path p, OpenOption... options) {
        return newOutputStream(newFileChannel(p, options), true);
    }

    public static FileChannel newFileChannel(Path p, OpenOption... options) throws IOException {
        return FileChannel.open(p, options);
    }
}
