    }

    private void startShellProcess() throws IOException {
        ProcessBuilder builder = new ProcessBuilder(mIsRoot ? "su" : "sh");
        mConnection = builder.start();
        mStdInput = new DataOutputStream(mConnection.getOutputStream());
        mStdOutput = new BufferedReader(new InputStreamReader(mConnection.getInputStream()));
        mIsActive = true;
        mStdOutputWorker = new Thread(() -> {
            try {
                String line;
                while ((line = mStdOutput.readLine()) != null) {
                    if (line.contains(mCommandEnd)) {
                        int exitCode = Integer.parseInt(line.replace(mCommandEnd, "").trim());
                        mListener.onStreamStop(exitCode);
                        mCounter.decrease();
                    } else {
                        mListener.onStreamInput(line);
                    }
                }
            } catch (IOException e) {
                mListener.onStreamDied();
            }
        });
        mStdOutputWorker.start();
    }

    public void execute(String command) {
        synchronized (mLock) {
            if (!mIsActive) return;
            mCounter.increase();
            mListener.onStreamStart();
            try {
                mStdInput.writeBytes(command + "\necho " + mCommandEnd + "$?\n");
                mStdInput.flush();
            } catch (IOException e) {
                mListener.onStreamDied();
            }
        }
    }

    public boolean waitFor() {
        return waitFor(null);
    }

    public boolean waitFor(Integer timeout) {
        return waitFor(timeout, null);
    }

    private boolean waitFor(Integer timeout, Integer index) {
        long startTime = System.currentTimeMillis();
        while (true) {
            if (mCounter.size() == 0) return true;
            if (timeout != null && System.currentTimeMillis() - startTime > timeout) return false;
            try {
                Thread.sleep(100);
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
                return false;
            }
        }
    }

    public boolean isActive() {
        return mIsActive;
    }

    public boolean isRunning() {
        return mCounter.size() > 0;
    }

    public boolean isRoot() {
        return mIsRoot;
    }

    public void destroy() {
        synchronized (mLock) {
            if (!mIsActive) return;
            mIsActive = false;
            try {
                mStdInput.close();
                mStdOutput.close();
                mConnection.destroy();
            } catch (IOException e) {
                // Ignore
            }
            mListener.onStreamDied();
        }
    }

    public interface OnStreamListener {
        void onStreamStart();
        void onStreamInput(String line);
        void onStreamStop(Integer exitCode);
        void onStreamDied();
    }

    private static class Counter {
        private AtomicInteger count = new AtomicInteger(0);

        public int size() {
            return count.get();
        }

        public int increase() {
            return count.incrementAndGet();
        }

        public int decrease() {
            return count.decrementAndGet();
        }

        public void reset() {
            count.set(0);
        }
    }
}
