public class ProcessExecutor {

    public static Collection<String> runWith(List<String> cmd) throws IOException, InterruptedException {
        ProcessBuilder pb = new ProcessBuilder(cmd);
        Process process = pb.start();

        List<String> output = new ArrayList<>();
        List<String> error = new ArrayList<>();

        InputStreamDrainer stdOutDrainer = new InputStreamDrainer(process.getInputStream(), System.out, output);
        InputStreamDrainer stdErrDrainer = new InputStreamDrainer(process.getErrorStream(), System.err, error);

        stdOutDrainer.start();
        stdErrDrainer.start();

        int exitCode = process.waitFor();
        stdOutDrainer.join();
        stdErrDrainer.join();

        if (exitCode != 0) {
            throw new RuntimeException("Process exited with code " + exitCode + ": " + String.join("\n", error));
        }

        return output;
    }

    public static Process runAsync(String... cmd) throws IOException {
        ProcessBuilder pb = new ProcessBuilder(cmd);
        return pb.start();
    }

    private static class InputStreamDrainer extends Thread {
        private final InputStream inputStream;
        private final OutputStream outputStream;
        private final List<String> outputList;

        public InputStreamDrainer(InputStream inputStream, OutputStream outputStream, List<String> outputList) {
            this.inputStream = inputStream;
            this.outputStream = outputStream;
            this.outputList = outputList;
        }

        @Override
        public void run() {
