        // Capture and log standard output
        BufferedReader stdOutReader = new BufferedReader(new InputStreamReader(process.getInputStream()));
        String line;
        try (FileWriter outWriter = new FileWriter(diffFile)) {
            while ((line = stdOutReader.readLine()) != null) {
                System.out.println(line); // Log to console
                outWriter.write(line + System.lineSeparator()); // Write to diff file
            }
        }

        // Capture and log standard error
        BufferedReader stdErrReader = new BufferedReader(new InputStreamReader(process.getErrorStream()));
        while ((line = stdErrReader.readLine()) != null) {
            System.err.println(line); // Log to console
        }

        // Wait for the process to complete
        int exitCode = process.waitFor();
        if (exitCode != 0) {
            throw new IOException("Diff process exited with code " + exitCode);
        }
    }
