import java.io.IOException;
import java.nio.MappedByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.*;
import java.util.*;
import java.util.concurrent.ConcurrentHashMap;
import java.util.logging.Logger;

public class DaemonRegistry implements AutoCloseable {
    private static final Logger LOGGER = Logger.getLogger(DaemonRegistry.class.getName());
    private static final int MAX_LENGTH = 32 * 1024; // 32KB
    private static final long LOCK_TIMEOUT_MS = 20 * 1000; // 20 seconds
    private static final Map<Path, Object> locks = new ConcurrentHashMap<>();
    private final Path registryFile;
    private final Object lck = new Object();
    private FileChannel channel;
    private MappedByteBuffer buffer;
    private long size;
    private Map<String, DaemonInfo> infosMap;

    public DaemonRegistry(Path registryFile) throws IOException {
        this.registryFile = registryFile;
        Files.createDirectories(registryFile.getParent());
        this.channel = FileChannel.open(registryFile, StandardOpenOption.CREATE, StandardOpenOption.READ, StandardOpenOption.WRITE);
        this.size = channel.size();
        if (size == 0) {
            size = MAX_LENGTH;
            channel.truncate(size);
        }
        this.buffer = channel.map(FileChannel.MapMode.READ_WRITE, 0, size);
        this.infosMap = new HashMap<>();
        read();
    }

    @Override
    public void close() throws IOException {
        synchronized (lck) {
            if (buffer != null) {
                buffer.force();
                buffer = null;
            }
            if (channel != null) {
                channel.close();
                channel = null;
            }
        }
    }

    public List<DaemonInfo> getAll() {
        synchronized (infosMap) {
            return new ArrayList<>(infosMap.values());
        }
    }

    private void read() {
        synchronized (infosMap) {
            infosMap.clear();
            buffer.position(0);
            while (buffer.hasRemaining()) {
                String id = readString();
                if (id.isEmpty()) break;
                String status = readString();
                DaemonInfo info = new DaemonInfo(id, status);
                infosMap.put(id, info);
            }
        }
    }

    private void doUpdate(Runnable updater) throws IOException {
        synchronized (lck) {
            FileLock lock = tryLock();
            if (lock == null) {
                throw new IOException("Failed to acquire lock on the registry file.");
            }
            try {
                updater.run();
                buffer.force();
            } finally {
                lock.release();
            }
        }
    }

    private FileLock tryLock() throws IOException {
        long start = System.currentTimeMillis();
        while (System.currentTimeMillis() - start < LOCK_TIMEOUT_MS) {
            try {
                return channel.tryLock();
            } catch (OverlappingFileLockException e) {
                Thread.onSpinWait();
            }
        }
        return null;
    }

    private void reset() {
        synchronized (lck) {
            synchronized (infosMap) {
                infosMap.clear();
                buffer.position(0);
                buffer.limit(buffer.capacity());
                buffer.clear();
            }
        }
    }

    private String readString() {
        StringBuilder sb = new StringBuilder();
        while (buffer.hasRemaining()) {
            byte b = buffer.get();
            if (b == 0) break;
            sb.append((char) b);
        }
        return sb.toString();
    }

    private void writeString(String str) {
        byte[] bytes = str.getBytes();
        buffer.put(bytes);
        buffer.put((byte) 0); // Null terminator
    }

    private long nextPowerOf2(long a, long min) {
        long result = Math.max(a, min - 1);
        result |= result >> 1;
        result |= result >> 2;
        result |= result >> 4;
        result |= result >> 8;
        result |= result >> 16;
        return result + 1;
    }
}
