        PowerManager powerManager = (PowerManager) context.getSystemService(Context.POWER_SERVICE);
        mWakeLock = powerManager.newWakeLock(PowerManager.PARTIAL_WAKE_LOCK, "ProcessHandler:WakeLock");
    }

    public Process setupAndLaunch(HashMap<String, String> env, String[] command) throws IOException {
        ProcessBuilder builder = new ProcessBuilder(command);
        if (mUseRoot) {
            builder.command("su", "-c", String.join(" ", command));
        }
        builder.environment().putAll(env);
        return builder.start();
    }

    public Thread log(InputStream is, int priority) {
        return new Thread(() -> {
            BufferedReader reader = new BufferedReader(new InputStreamReader(is));
            String line;
            try {
                while ((line = reader.readLine()) != null) {
                    switch (priority) {
                        case Log.DEBUG:
                            Log.d("ProcessHandler", line);
                            break;
                        case Log.ERROR:
                            Log.e("ProcessHandler", line);
                            break;
                        case Log.INFO:
                            Log.i("ProcessHandler", line);
                            break;
                        case Log.VERBOSE:
                            Log.v("ProcessHandler", line);
                            break;
                        case Log.WARN:
                            Log.w("ProcessHandler", line);
                            break;
                    }
                }
            } catch (IOException e) {
                Log.e("ProcessHandler", "Error reading input stream", e);
            }
        });
    }

    public String runProcess(ProcessBuilder pb, boolean returnStdOut) {
        mWakeLock.acquire();
        StringBuilder stdOut = new StringBuilder();
        Process process = null;
        try {
            process = pb.start();
            if (returnStdOut) {
                InputStream stdout = process.getInputStream();
                BufferedReader reader = new BufferedReader(new InputStreamReader(stdout));
                String line;
                while ((line = reader.readLine()) != null) {
                    stdOut.append(line).append("\n");
                }
            }
            log(process.getErrorStream(), Log.ERROR).start();
            log(process.getInputStream(), Log.INFO).start();
            process.waitFor();
        } catch (IOException | InterruptedException e) {
            Log.e("ProcessHandler", "Error running process", e);
        } finally {
            if (process != null) {
                process.destroy();
            }
            mWakeLock.release();
        }
        return stdOut.toString();
    }

    public List<String> getProcessPIDs(String processName) {
        List<String> pids = new ArrayList<>();
        try {
            ProcessBuilder builder = new ProcessBuilder("ps");
            if (mUseRoot) {
                builder.command("su", "-c", "ps");
            }
            Process process = builder.start();
            BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()));
            String line;
            while ((line = reader.readLine()) != null) {
                if (line.contains(processName)) {
                    String[] parts = line.split("\\s+");
                    pids.add(parts[1]);
                }
            }
            process.waitFor();
        } catch (IOException | InterruptedException e) {
            Log.e("ProcessHandler", "Error getting process PIDs", e);
        }
        return pids;
    }
}
