import io.lettuce.core.RedisClient;
import io.lettuce.core.api.StatefulRedisConnection;
import io.lettuce.core.api.sync.RedisCommands;
import org.reactivestreams.Publisher;
import reactor.adapter.rxjava.RxJava2Adapter;
import reactor.core.publisher.Flux;

public class RedisPublisher {
    private RedisClient client;
    private StatefulRedisConnection<String, String> connection;

    public RedisPublisher() {
        this.client = RedisClient.create("redis://localhost:6379");
        this.connection = client.connect();
        RedisCommands<String, String> syncCommands = connection.sync();
        syncCommands.flushall();
    }

    public Publisher<String> createPublisher(long elements) {
        if (elements == Long.MAX_VALUE) {
            return null;
        }
        String key = "reactiveList";
        RedisCommands<String, String> syncCommands = connection.sync();
        for (long i = 0; i < elements; i++) {
            syncCommands.rpush(key, "Element-" + i);
        }
        return RxJava2Adapter.monoToFlux(RxJava2Adapter.publisherToMono(Flux.create(sink -> {
            long index = 0;
            while (index < elements) {
                String element = syncCommands.lrange(key, index, index).get(0);
                sink.next(element);
                index++;
            }
            sink.complete();
        })));
    }

    public void close() {
        connection.close();
        client.shutdown();
    }
}
