import java.util.*;

public class ClusterConnectionProvider {
    private JedisClusterInfoCache cache;

    public ClusterConnectionProvider() {
        this.cache = new JedisClusterInfoCache();
    }

    public void initializeSlotsCache(Set<HostAndPort> nodes, JedisClientConfig clientConfig) {
        JedisPoolConfig poolConfig = new JedisPoolConfig();
        JedisCluster jedisCluster = new JedisCluster(nodes, clientConfig, poolConfig);
        cache.discoverClusterSlots(jedisCluster.getClusterNodes());
    }

    public Connection getConnection(CommandArguments args) {
        int slot = CRC16.getCRC16(args.getKey().getBytes()) & 0x3FFF;
        return getConnectionFromSlot(slot);
    }

    public Connection getConnection() {
        List<HostAndPort> nodes = cache.getNodes();
        if (nodes.isEmpty()) throw new IllegalStateException("No nodes available");
        HostAndPort randomNode = nodes.get(new Random().nextInt(nodes.size()));
        return cache.getConnectionPool(randomNode).getResource();
    }

    public Connection getConnectionFromSlot(int slot) {
        HostAndPort targetNode = cache.getSlotPool(slot).getHostAndPort();
        return cache.getConnectionPool(targetNode).getResource();
    }

    public void renewSlotCache() {
        cache.renewClusterSlots();
    }

    public void close() {
        cache.close();
    }

    public Map<String, ConnectionPool> getConnectionMap() {
        return Collections.unmodifiableMap(cache.getNodeToConnectionPoolMap());
    }
}
