            switch (command.getType()) {
                case SET:
                    jedis.set(command.getKey(), command.getValue());
                    break;
                case GET:
                    String value = jedis.get(command.getKey());
                    command.setValue(value);
                    break;
                case INCR:
                    long incrementedValue = jedis.incr(command.getKey());
                    command.setValue(String.valueOf(incrementedValue));
                    break;
                default:
