import io.lettuce.core.RedisClient;
import io.lettuce.core.RedisURI;
import io.lettuce.core.api.StatefulRedisConnection;
import io.lettuce.core.support.ConnectionPoolSupport;
import io.lettuce.core.support.GenericObjectPoolConfig;

import javax.net.ssl.*;
import java.io.FileInputStream;
import java.security.KeyStore;

public class RedisSSLConnection {
    private RedisClient redisClient;
    private StatefulRedisConnection<String, String> connection;

    public RedisSSLConnection(String redisUri, String hostName, String truststorePath, String truststorePassword) throws Exception {
        // Load the truststore
        KeyStore truststore = KeyStore.getInstance(KeyStore.getDefaultType());
        try (FileInputStream fis = new FileInputStream(truststorePath)) {
            truststore.load(fis, truststorePassword.toCharArray());
        }

        // Initialize TrustManagerFactory with the truststore
        TrustManagerFactory tmf = TrustManagerFactory.getInstance(TrustManagerFactory.getDefaultAlgorithm());
        tmf.init(truststore);

        // Create SSLContext and initialize it with the TrustManagerFactory
        SSLContext sslContext = SSLContext.getInstance("TLS");
        sslContext.init(null, tmf.getTrustManagers(), null);

        // Create SSLParameters and set the SNI hostname
        SSLParameters sslParameters = new SSLParameters();
        sslParameters.setEndpointIdentificationAlgorithm("HTTPS");
        sslParameters.setServerNames(Collections.singletonList(new SNIServerName(SNIHostName.TYPE, hostName)));

        // Configure RedisURI with SSL settings
        RedisURI uri = RedisURI.Builder.redis(redisUri)
                .withSsl(true)
                .withSslSocketFactory(sslContext.getSocketFactory())
                .withSslParameters(sslParameters)
                .build();

        // Initialize RedisClient
        this.redisClient = RedisClient.create(uri);
        this.connection = redisClient.connect();
    }

    public StatefulRedisConnection<String, String> getConnection() {
        return connection;
    }

    public void close() {
        if (connection != null) {
            connection.close();
        }
        if (redisClient != null) {
            redisClient.shutdown();
        }
    }
