    private static final Logger logger = LoggerFactory.getLogger(JedisUtil.class);
    private String address;
    private ShardedJedisPool shardedJedisPool;
    private static final ReentrantLock INSTANCE_INIT_LOCK = new ReentrantLock();

    public void init(String address) {
        INSTANCE_INIT_LOCK.lock();
        try {
            if (this.address == null || !this.address.equals(address)) {
                this.address = address;
                List<JedisShardInfo> shards = new ArrayList<>();
                String[] addresses = address.split(",");
                for (String addr : addresses) {
                    String[] parts = addr.split(":");
                    shards.add(new JedisShardInfo(parts[0], Integer.parseInt(parts[1])));
                }
                shardedJedisPool = new ShardedJedisPool(null, shards);
            }
        } finally {
            INSTANCE_INIT_LOCK.unlock();
        }
    }

    public ShardedJedis getInstance() {
        if (shardedJedisPool == null) {
            throw new IllegalStateException("JedisUtil has not been initialized.");
        }
        return shardedJedisPool.getResource();
    }

    public void close() {
        if (shardedJedisPool != null) {
            shardedJedisPool.close();
        }
    }

    public byte[] serialize(Object object) {
        try (ByteArrayOutputStream bos = new ByteArrayOutputStream();
             ObjectOutputStream oos = new ObjectOutputStream(bos)) {
            oos.writeObject(object);
            return bos.toByteArray();
        } catch (Exception e) {
            logger.error("Error serializing object", e);
            throw new RuntimeException("Error serializing object", e);
        }
    }

    public Object unserialize(byte[] bytes) {
        try (ByteArrayInputStream bis = new ByteArrayInputStream(bytes);
             ObjectInputStream ois = new ObjectInputStream(bis)) {
            return ois.readObject();
        } catch (Exception e) {
            logger.error("Error deserializing object", e);
            throw new RuntimeException("Error deserializing object", e);
        }
    }

    public String setStringValue(String key, String value, int seconds) {
        try (ShardedJedis jedis = getInstance()) {
            return jedis.setex(key, seconds, value);
        }
    }

    public String setObjectValue(String key, Object obj, int seconds) {
        try (ShardedJedis jedis = getInstance()) {
            return jedis.setex(key.getBytes(), seconds, serialize(obj));
        }
    }

    public String getStringValue(String key) {
        try (ShardedJedis jedis = getInstance()) {
            return jedis.get(key);
        }
    }

    public Object getObjectValue(String key) {
        try (ShardedJedis jedis = getInstance()) {
            byte[] bytes = jedis.get(key.getBytes());
            return bytes != null ? unserialize(bytes) : null;
        }
