import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;

public class WaveWriter implements AutoCloseable {
    private AudioFormat mAudioFormat;
    private int mFileRolloverCounter;
    private long mMaxSize;
    private Path mFile;
    private FileChannel mFileChannel;
    private boolean mDataChunkOpen;
    private long mDataChunkSizeOffset;
    private int mDataChunkSize;

    public WaveWriter(AudioFormat format, Path file, long maxSize) {
        mAudioFormat = format;
        mFile = file;
        mMaxSize = maxSize;
        open();
    }

    public WaveWriter(AudioFormat format, Path file) {
        this(format, file, 0);
    }

    private void open() {
        try {
            mFileChannel = FileChannel.open(mFile, StandardOpenOption.CREATE, StandardOpenOption.WRITE);
            mFileChannel.write(getWaveHeader(mAudioFormat));
            openDataChunk();
        } catch (IOException e) {
            throw new RuntimeException("Failed to open file channel", e);
        }
    }

    @Override
    public void close() {
        close(mFile);
    }

    public void close(Path path) {
        try {
            if (mDataChunkOpen) {
                closeDataChunk();
            }
            mFileChannel.close();
            Files.move(mFile, path);
        } catch (IOException e) {
            throw new RuntimeException("Failed to close file channel", e);
        }
    }

    public void writeData(ByteBuffer buffer) throws IOException {
        if (mMaxSize > 0 && mFileChannel.size() + buffer.remaining() > mMaxSize) {
            rollover();
        }
        mFileChannel.write(buffer);
        mDataChunkSize += buffer.remaining();
    }

    private void closeDataChunk() {
        updateDataChunkSize();
        mDataChunkOpen = false;
    }

    private void openDataChunk() {
        try {
            ByteBuffer dataHeader = getDataHeader();
            mFileChannel.write(dataHeader);
            mDataChunkSizeOffset = mFileChannel.position();
            mDataChunkSize = 0;
            mDataChunkOpen = true;
        } catch (IOException e) {
            throw new RuntimeException("Failed to open data chunk", e);
        }
    }

    private void rollover() {
        closeDataChunk();
        updateTotalSize();
        close();
        updateFileName();
        open();
    }

    private void updateTotalSize() {
        try {
            long fileSize = mFileChannel.size();
            ByteBuffer sizeBuffer = getUnsignedIntegerBuffer(fileSize - 8);
            mFileChannel.position(4);
            mFileChannel.write(sizeBuffer);
        } catch (IOException e) {
            throw new RuntimeException("Failed to update total size", e);
        }
    }

    private void updateDataChunkSize() {
        try {
            ByteBuffer sizeBuffer = getUnsignedIntegerBuffer(mDataChunkSize);
            mFileChannel.position(mDataChunkSizeOffset);
            mFileChannel.write(sizeBuffer);
        } catch (IOException e) {
            throw new RuntimeException("Failed to update data chunk size", e);
        }
    }

    protected static ByteBuffer getUnsignedIntegerBuffer(long size) {
        return ByteBuffer.allocate(4).putInt((int) size).flip();
    }

    private void updateFileName() {
        mFileRolloverCounter++;
        String fileName = mFile.getFileName().toString();
        String baseName = fileName.substring(0, fileName.lastIndexOf('.'));
        String extension = fileName.substring(fileName.lastIndexOf('.'));
        mFile = mFile.resolveSibling(baseName + "_" + mFileRolloverCounter + extension);
    }

    public static ByteBuffer getDataHeader() {
        ByteBuffer header = ByteBuffer.allocate(8);
        header.put("data".getBytes());
        header.putInt(0); // Placeholder for size, will be updated later
        return header.flip();
    }

    public static ByteBuffer getWaveHeader(AudioFormat format) {
        ByteBuffer header = ByteBuffer.allocate(44);
        header.put("RIFF".getBytes());
        header.putInt(0); // Placeholder for size, will be updated later
        header.put("WAVE".getBytes());
        header.put("fmt ".getBytes());
        header.putInt(16); // Subchunk1Size
        header.putShort((short) format.getChannels());
        header.putShort((short) format.getSampleRate());
        header.putInt(format.getSampleRate() * format.getChannels() * format.getSampleSizeInBits() / 8);
        header.putShort((short) (format.getChannels() * format.getSampleSizeInBits() / 8));
        header.putShort((short) format.getSampleSizeInBits());
        header.put("data".getBytes());
        header.putInt(0); // Placeholder for size, will be updated later
        return header.flip();
    }

    public static ByteBuffer getFormatChunk(AudioFormat format) {
        ByteBuffer chunk = ByteBuffer.allocate(24);
        chunk.put("fmt ".getBytes());
        chunk.putInt(16); // Subchunk1Size
        chunk.putShort((short) format.getChannels());
        chunk.putShort((short) format.getSampleRate());
        chunk.putInt(format.getSampleRate() * format.getChannels() * format.getSampleSizeInBits() / 8);
        chunk.putShort((short) (format.getChannels() * format.getSampleSizeInBits() / 8));
        chunk.putShort((short) format.getSampleSizeInBits());
        return chunk.flip();
    }
}
