    public RedisCommands<String, String> connect() {
        RedisCommands<String, String> connect = client.connect().sync();
        return connect;
    }

    public void openConnection() throws Exception {
        client.setOptions(ClientOptions.builder().build());
        redis = connect();
        boolean scriptRunning;
        do {
            scriptRunning = false;
            try {
                redis.flushall();
                redis.flushdb();
            } catch (RedisBusyException e) {
                scriptRunning = true;
                try {
                    redis.scriptKill();
                } catch (RedisException e1) {
                    // Script might have ended between BUSY check and KILL attempt
                }
            }
        } while (scriptRunning);
    }

    public void closeConnection() throws Exception {
        if (redis != null) {
            redis.getStatefulConnection().close();
        }
    }
}

