import io.lettuce.core.*;
import io.lettuce.core.api.StatefulRedisConnection;
import io.lettuce.core.api.StatefulRedisClusterConnection;
import io.lettuce.core.api.StatefulRedisPubSubConnection;
import io.lettuce.core.cluster.RedisClusterClient;
import io.lettuce.core.cluster.ClusterClientOptions;
import io.lettuce.core.support.ConnectionPoolSupport;
import io.lettuce.core.support.GenericObjectPool;
import io.lettuce.core.support.GenericObjectPoolConfig;

public class RedisConnectionManager {

    private ClusterClientOptions clientOptions;
    private TokenBasedRedisCredentialsProvider credentialsProvider;
    private RedisClient client;
    private RedisClusterClient clusterClient;
    private GenericObjectPool<StatefulRedisConnection<String, String>> standaloneConnectionPool;
    private GenericObjectPool<StatefulRedisClusterConnection<String, String>> clusterConnectionPool;
    private GenericObjectPool<StatefulRedisPubSubConnection<String, String>> pubSubConnectionPool;

    public RedisConnectionManager(ClusterClientOptions clientOptions, TokenBasedRedisCredentialsProvider credentialsProvider) {
        this.clientOptions = clientOptions;
        this.credentialsProvider = credentialsProvider;
    }

    public void initializeConnection(EntraIdTestContext testCtx) {
        // Initialize standalone Redis client
        this.client = RedisClient.create("redis://localhost:6379");
        this.client.setOptions(clientOptions);
        this.client.setDefaultCredentialsProvider(credentialsProvider);

        // Initialize Redis cluster client
        this.clusterClient = RedisClusterClient.create("redis://localhost:7000,redis://localhost:7001");
        this.clusterClient.setOptions(clientOptions);
        this.clusterClient.setDefaultCredentialsProvider(credentialsProvider);

        // Setup connection pools
        standaloneConnectionPool = ConnectionPoolSupport.newGenericObjectPool(() -> client.connect(), new GenericObjectPoolConfig<>());
        clusterConnectionPool = ConnectionPoolSupport.newGenericObjectPool(() -> clusterClient.connect(), new GenericObjectPoolConfig<>());
        pubSubConnectionPool = ConnectionPoolSupport.newGenericObjectPool(() -> client.connectPubSub(), new GenericObjectPoolConfig<>());
    }

    public StatefulRedisConnection<String, String> createStandaloneConnection() {
        try {
            return standaloneConnectionPool.borrowObject();
        } catch (Exception e) {
            throw new RuntimeException("Failed to borrow standalone connection", e);
        }
    }

    public StatefulRedisClusterConnection<String, String> createClusterConnection() {
        try {
            return clusterConnectionPool.borrowObject();
        } catch (Exception e) {
            throw new RuntimeException("Failed to borrow cluster connection", e);
        }
    }

    public StatefulRedisPubSubConnection<String, String> createPubSubConnection() {
        try {
            return pubSubConnectionPool.borrowObject();
        } catch (Exception e) {
            throw new RuntimeException("Failed to borrow Pub/Sub connection", e);
        }
    }

    public void closeConnections() {
        try {
            standaloneConnectionPool.close();
            clusterConnectionPool.close();
            pubSubConnectionPool.close();
            client.shutdown();
            clusterClient.shutdown();
        } catch (Exception e) {
            throw new RuntimeException("Failed to close connections", e);
        }
    }
}
