import java.io.IOException;
import java.nio.MappedByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;

public class ArrayLongLongMapMmap {
    private static final int SEGMENT_SIZE = 1 << 27; // 128 MB
    private Path path;
    private FileChannel writeChannel;
    private MappedByteBuffer[] segmentsArray;
    private FileChannel readChannel;

    public ArrayLongLongMapMmap(Path path, boolean madvise) throws IOException {
        this.path = path;
        this.writeChannel = FileChannel.open(path, StandardOpenOption.CREATE, StandardOpenOption.WRITE);
        init();
    }

    private void init() throws IOException {
        writeChannel.close();
        readChannel = FileChannel.open(path, StandardOpenOption.READ);
        long size = Files.size(path);
        int segmentCount = (int) ((size + SEGMENT_SIZE - 1) / SEGMENT_SIZE);
        segmentsArray = new MappedByteBuffer[segmentCount];
        for (int i = 0; i < segmentCount; i++) {
            segmentsArray[i] = readChannel.map(FileChannel.MapMode.READ_ONLY, i * SEGMENT_SIZE, Math.min(SEGMENT_SIZE, size - i * SEGMENT_SIZE));
        }
    }

    public long get(long key) {
        int segmentIndex = (int) (key >>> 49); // Assuming key is 64 bits, 17 bits per segment
        if (segmentIndex >= segmentsArray.length) {
            return -1; // Key out of range
        }
        MappedByteBuffer segment = segmentsArray[segmentIndex];
        int offset = (int) (key & ((1L << 49) - 1)) * 8;
        if (offset + 8 > segment.capacity()) {
            return -1; // Key out of range
        }
        return segment.getLong(offset);
    }

    public void close() throws IOException {
        for (MappedByteBuffer segment : segmentsArray) {
            if (segment != null) {
                segment.force();
                segment.clear();
                segment = null;
            }
        }
        readChannel.close();
        Files.delete(path);
    }
}
