            System.err.println("Failed to parse command line arguments: " + e.getMessage());
            formatter.printHelp("TimerConsumer", options);
            System.exit(1);
        }
    }

    public void start() throws Exception {
        consumer.subscribe(topic, "*");
        consumer.registerMessageListener((MessageListenerConcurrently) (msgs, context) -> {
            for (MessageExt msg : msgs) {
                long storeTimestamp = msg.getStoreTimestamp();
                long consumeTimestamp = System.currentTimeMillis();
                long delay = consumeTimestamp - storeTimestamp;
                System.out.println("Received message: " + new String(msg.getBody()) +
                        ", Delay: " + delay + " ms, Store Time: " + new Date(storeTimestamp) +
                        ", Consume Time: " + new Date(consumeTimestamp));
