import java.net.URI;
import java.net.http.HttpClient;
import java.net.http.HttpRequest;
import java.net.http.HttpResponse;
import java.net.http.HttpRequest.BodyPublishers;
import java.net.http.HttpResponse.BodyHandlers;
import java.time.Duration;
import java.util.concurrent.CompletableFuture;

public class BroadcastifyHttpClient {

    private final HttpClient mHttpClient;

    public BroadcastifyHttpClient() {
        this.mHttpClient = HttpClient.newBuilder()
                .version(HttpClient.Version.HTTP_2)
                .connectTimeout(Duration.ofSeconds(20))
                .followRedirects(HttpClient.Redirect.NORMAL)
                .build();
    }

    public String testConnection(BroadcastifyCallConfiguration configuration) {
        HttpRequest request = HttpRequest.newBuilder()
                .uri(URI.create(configuration.getApiUrl()))
                .timeout(Duration.ofSeconds(20))
                .header("Content-Type", "multipart/form-data")
                .POST(BodyPublishers.ofString("api_key=" + configuration.getApiKey() +
                        "&system_id=" + configuration.getSystemId() +
                        "&test=true"))
                .build();

        try {
            HttpResponse<String> response = mHttpClient.send(request, BodyHandlers.ofString());
            return "Status Code: " + response.statusCode() + ", Response Body: " + response.body();
        } catch (Exception e) {
            return "Error: " + e.getMessage();
        }
    }

    public void sendAsyncRequest(HttpRequest request) {
        CompletableFuture<HttpResponse<String>> futureResponse = mHttpClient.sendAsync(request, BodyHandlers.ofString());

        futureResponse.thenAccept(response -> {
            if (response.statusCode() == 200) {
                System.out.println("Success: " + response.body());
            } else {
                System.err.println("Error: Status Code " + response.statusCode() + ", Message: " + response.body());
            }
        }).exceptionally(ex -> {
            System.err.println("Exception: " + ex.getMessage());
            return null;
        });
    }
}

