import java.io.*;
import java.net.Socket;
import org.ietf.jgss.*;

public class GSSClient {
    private Socket s;

    public static void main(String[] args) {
        if (args.length != 3) {
            usage();
            System.exit(1);
        }

        String host = args[0];
        int port = Integer.parseInt(args[1]);
        String serverName = args[2];

        try {
            GSSClient client = new GSSClient();
            client.s = new Socket(host, port);
            Oid mechOid = new Oid("1.2.840.113554.1.2.2"); // Kerberos V5
            GSSContext context = client.createCtxt(serverName, mechOid);

            String message = "Hello Server";
            client.sendMsgToPeer(context, message);

            BufferedReader reader = new BufferedReader(new InputStreamReader(client.s.getInputStream()));
            String response = reader.readLine();
            client.verifyMsgFromPeer(context, response);

            context.dispose();
            client.s.close();
        } catch (Exception e) {
            e.printStackTrace();
            System.exit(1);
        }
    }

    private GSSContext createCtxt(String serverName, Oid mechOid) throws Exception {
        GSSManager manager = GSSManager.getInstance();
        GSSName serverNameObj = manager.createName("host/" + serverName, GSSName.NT_HOSTBASED_SERVICE);
        GSSCredential clientCreds = manager.createCredential(null, GSSCredential.INITIATE_ONLY, mechOid, GSSCredential.DEFAULT_LIFETIME);

        GSSContext context = manager.createContext(serverNameObj, mechOid, clientCreds, GSSContext.DEFAULT_LIFETIME);
        return context;
    }

    private void sendMsgToPeer(GSSContext aCtxt, String msg) throws Exception {
        byte[] outToken = aCtxt.initSecContext(msg.getBytes(), 0, msg.length());
        OutputStream out = s.getOutputStream();
        out.write(outToken);
        out.flush();
    }

    private void verifyMsgFromPeer(GSSContext aCtxt, String msg) throws Exception {
        byte[] inToken = msg.getBytes();
        byte[] outToken = aCtxt.acceptSecContext(inToken, 0, inToken.length);
        if (outToken != null) {
            OutputStream out = s.getOutputStream();
            out.write(outToken);
            out.flush();
        }
    }

    private static void usage() {
        System.out.println("Usage: java GSSClient <host> <port> <serverName>");
    }

    private void print(String msg) {
        System.out.println(msg);
    }

    private void exit(int status) {
        try {
            if (s != null) s.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
        System.exit(status);
    }
}
