public class HttpGetTask extends AsyncTask<Void, Void, String> {

    private static final String TAG = "HttpGetTask";
    private static final String HOST = "example.com";
    private static final String USER_NAME = "user";
    private static final String HTTP_GET_COMMAND = "GET / HTTP/1.1\r\nHost: " + HOST + "\r\nAuthorization: Basic " + Base64.encodeToString((USER_NAME + ":").getBytes(), Base64.DEFAULT) + "\r\nConnection: close\r\n\r\n";

    private WeakReference<RetainedFragment> mListener;

    public HttpGetTask(RetainedFragment retainedFragment) {
        mListener = new WeakReference<>(retainedFragment);
    }

    @Override
    protected String doInBackground(Void... params) {
        Socket socket = null;
        InputStream inputStream = null;
        try {
            socket = new Socket(HOST, 80);
            OutputStream outputStream = socket.getOutputStream();
            outputStream.write(HTTP_GET_COMMAND.getBytes());
            outputStream.flush();

            inputStream = socket.getInputStream();
            return readStream(inputStream);
        } catch (IOException e) {
            Log.e(TAG, "Error during HTTP GET request", e);
            return null;
        } finally {
            try {
                if (inputStream != null) inputStream.close();
                if (socket != null) socket.close();
            } catch (IOException e) {
                Log.e(TAG, "Error closing resources", e);
            }
        }
    }

    @Override
    protected void onPostExecute(String result) {
        RetainedFragment fragment = mListener.get();
        if (fragment != null) {
            fragment.onHttpGetResult(result);
        }
    }

    private String readStream(InputStream in) throws IOException {
        BufferedReader reader = new BufferedReader(new InputStreamReader(in));
        StringBuilder response = new StringBuilder();
        String line;
        while ((line = reader.readLine()) != null) {
            response.append(line).append("\n");
        }
