            init();
            if (mOnSendListener != null) {
                mOnSendListener.onStart();
            }
            parseHeader();
            parseBody();
            finish();
            if (mOnSendListener != null) {
                mOnSendListener.onSuccess(mFileInfo);
            }
        } catch (Exception e) {
            if (mOnSendListener != null) {
                mOnSendListener.onFailure(e, mFileInfo);
            }
        }
    }

    private void init() throws IOException {
        mSocket = new Socket(mServerIpAddress, mPort);
        mOutputStream = new BufferedOutputStream(mSocket.getOutputStream());
    }

    private void parseHeader() throws IOException {
        String header = "FILE_INFO:" + mFileInfo.getFileName() + ":" + mFileInfo.getFileSize();
        mOutputStream.write(header.getBytes());
        mOutputStream.flush();
    }

    private void parseBody() throws IOException {
        byte[] buffer = new byte[1024];
        long totalBytesSent = 0;
        try (InputStream inputStream = Files.newInputStream(Paths.get(mFileInfo.getFilePath()))) {
            int bytesRead;
            while ((bytesRead = inputStream.read(buffer)) != -1) {
                mOutputStream.write(buffer, 0, bytesRead);
                totalBytesSent += bytesRead;
                if (mOnSendListener != null) {
                    mOnSendListener.onProgress(totalBytesSent, mFileInfo.getFileSize());
                }
            }
        }
    }

    private void finish() throws IOException {
        if (mOutputStream != null) {
            mOutputStream.close();
        }
        if (mSocket != null) {
            mSocket.close();
        }
    }

    public interface OnSendListener {
        void onStart();
        void onProgress(long bytesSent, long totalBytes);
        void onSuccess(FileInfo fileInfo);
        void onFailure(Throwable throwable, FileInfo fileInfo);
    }
}
