                    apiCallId |= (inputStream.read() & 0xFF) << 8;
                    apiCallId |= (inputStream.read() & 0xFF) << 16;
                    apiCallId |= (inputStream.read() & 0xFF) << 24;
                    int length = inputStream.read() & 0xFF;
                    length |= (inputStream.read() & 0xFF) << 8;
                    length |= (inputStream.read() & 0xFF) << 16;
                    length |= (inputStream.read() & 0xFF) << 24;
