import java.util.List;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;

public class HttpCommunicationClient {
    private InetSocketAddress address;
    private HttpClient httpClient;

    public HttpCommunicationClient(InetSocketAddress address) {
        this.address = address;
    }

    public void setupHttpClient(ExecutorService threadPool) {
        this.httpClient = HttpClient.newBuilder()
                .executor(threadPool)
                .followRedirects(HttpClient.Redirect.ALWAYS)
                .build();
    }

    public void closeClient() {
        if (httpClient != null) {
            ((ExecutorService) httpClient.executor()).shutdown();
        }
    }

    private HttpResponse<String> executeAndDefaultAssertions(HttpRequest request) throws Exception {
        return httpClient.send(request, HttpResponse.BodyHandlers.ofString());
    }

    public HttpResponse<String> get(String path, String[]... headers) throws Exception {
        HttpRequest.Builder requestBuilder = HttpRequest.newBuilder()
                .uri(new URI("http://" + address.getHostString() + ":" + address.getPort() + path))
                .GET();

        for (String[] header : headers) {
            requestBuilder.header(header[0], header[1]);
        }

        return executeAndDefaultAssertions(requestBuilder.build());
