    public HttpResponse<String> post(String path) throws Exception {
        HttpRequest request = HttpRequest.newBuilder()
                .uri(new URI("http://" + address.getHostString() + ":" + address.getPort() + path))
                .POST(HttpRequest.BodyPublishers.noBody())
                .build();

        return executeAndDefaultAssertions(request);
    }

    public List<String> getAllRedirectLocations(String path, String[]... headers) throws Exception {
        List<String> redirectLocations = new ArrayList<>();
        HttpRequest.Builder requestBuilder = HttpRequest.newBuilder()
                .uri(new URI("http://" + address.getHostString() + ":" + address.getPort() + path))
                .GET();

        for (String[] header : headers) {
            requestBuilder.header(header[0], header[1]);
        }

        HttpRequest request = requestBuilder.build();
        HttpResponse<String> response = httpClient.send(request, HttpResponse.BodyHandlers.ofString());

        while (response.statusCode() == 302 || response.statusCode() == 303) {
            redirectLocations.add(response.headers().firstValue("Location").orElse(""));
            request = HttpRequest.newBuilder()
                    .uri(new URI(redirectLocations.get(redirectLocations.size() - 1)))
                    .GET()
                    .build();
            response = httpClient.send(request, HttpResponse.BodyHandlers.ofString());
        }

        return redirectLocations;
    }

    public static String[] browserHeader() {
        return new String[]{"User-Agent", "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/58.0.3029.110 Safari/537.3"};
    }
