import java.nio.charset.StandardCharsets;

public class HttpClient {

    private static final String HOST = "localhost";
    private static final int PORT = 8080;

    public void connectAndUpgrade(String url) throws IOException {
        Socket socket = new Socket(HOST, PORT);
        OutputStream outputStream = socket.getOutputStream();
        InputStream inputStream = socket.getInputStream();

        // Send HTTP upgrade request
        String upgradeRequest = "GET " + url + " HTTP/1.1\r\n" +
                                "Host: localhost\r\n" +
                                "Connection: Upgrade\r\n" +
                                "Upgrade: websocket\r\n\r\n";
        outputStream.write(upgradeRequest.getBytes(StandardCharsets.UTF_8));
        outputStream.flush();

        // Read server response
        byte[] responseBytes = readBytes(inputStream);
        String response = new String(responseBytes, StandardCharsets.UTF_8);

        // Assert server response
        assert response.contains("HTTP/1.1 101 Switching Protocols") : "Failed to upgrade connection";

        // Send echo message
        String echoMessage = "Hello, Server!";
        outputStream.write(echoMessage.getBytes(StandardCharsets.UTF_8));
        outputStream.flush();

        // Read echo response
        byte[] echoResponseBytes = readBytes(inputStream);
        String echoResponse = new String(echoResponseBytes, StandardCharsets.UTF_8);

        // Assert echo response
        assert echoResponse.equals(echoMessage) : "Echo response mismatch";

        // Close connection
        socket.close();
    }

    private byte[] readBytes(InputStream inputStream) throws IOException {
        ByteArrayOutputStream byteArrayOutputStream = new ByteArrayOutputStream();
        byte[] buffer = new byte[1024];
        int bytesRead;
        while ((bytesRead = inputStream.read(buffer)) != -1) {
            byteArrayOutputStream.write(buffer, 0, bytesRead);
        }
        return byteArrayOutputStream.toByteArray();
    }
}
