public class SocketClient {
    private static SocketClient socketClient;
    private Socket socket;
    private OutputStream outputStream;
    private InputStream inputStream;
    private boolean isConnected;
    private static final int DEFAULT_READ_BUFFER_SIZE = 32 * 1024;
    private byte[] buffer = new byte[DEFAULT_READ_BUFFER_SIZE];
    private byte[] receivedData;

    private SocketClient() {}

    public static synchronized SocketClient getInstance() {
        if (socketClient == null) {
            socketClient = new SocketClient();
        }
        return socketClient;
    }

    public void openConnection(String ip, int port) throws IOException {
        if (isConnected) {
            throw new IllegalStateException("Already connected.");
        }
        socket = new Socket(ip, port);
        outputStream = socket.getOutputStream();
        inputStream = socket.getInputStream();
        isConnected = true;
    }

    public boolean isConnected() {
        return isConnected;
    }

    public void write(byte[] data) throws IOException {
        if (!isConnected) {
            throw new IllegalStateException("Not connected.");
        }
        outputStream.write(data);
        outputStream.flush();
    }

    public int read(int bytesToBeRead) throws IOException {
        if (!isConnected) {
            throw new IllegalStateException("Not connected.");
        }
        int bytesRead = inputStream.read(buffer, 0, Math.min(bytesToBeRead, buffer.length));
        if (bytesRead > 0) {
            receivedData = Arrays.copyOfRange(buffer, 0, bytesRead);
        } else {
            receivedData = new byte[0];
        }
        return bytesRead;
    }

    public byte[] getReceivedData() {
        return receivedData;
    }

    public void closeConnection() throws IOException {
        if (isConnected) {
            inputStream.close();
            outputStream.close();
            socket.close();
            isConnected = false;
        }
    }
