        reportOffset.clear();
        reportOffset.putLong(maxOffset);
        reportOffset.flip();
        try {
            socketChannel.write(reportOffset);
            currentReportedOffset = maxOffset;
            lastWriteTimestamp = System.currentTimeMillis();
            return true;
        } catch (IOException e) {
            e.printStackTrace();
            return false;
        }
    }

    private void reallocateByteBuffer() {
        byteBufferBackup = ByteBuffer.allocate(READ_MAX_BUFFER_SIZE);
    }

    private void swapByteBuffer() {
        ByteBuffer temp = byteBufferRead;
        byteBufferRead = byteBufferBackup;
        byteBufferBackup = temp;
    }

    private boolean processReadEvent() {
        try {
            int bytesRead = socketChannel.read(byteBufferRead);
            if (bytesRead == -1) {
                closeMaster();
                return false;
            }
            lastReadTimestamp = System.currentTimeMillis();
            return true;
        } catch (IOException e) {
            e.printStackTrace();
            return false;
        }
    }

    private boolean connectMaster() {
        try {
            socketChannel = SocketChannel.open();
            socketChannel.configureBlocking(false);
            socketChannel.connect(new InetSocketAddress(masterHaAddress.get(), 10911));
            socketChannel.register(selector, SelectionKey.OP_CONNECT);
            return true;
        } catch (IOException e) {
            e.printStackTrace();
            return false;
        }
    }

    private void closeMaster() {
        try {
            if (socketChannel != null) {
                socketChannel.close();
            }
            if (selector != null) {
                selector.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    @Override
    public void run() {
        while (!Thread.currentThread().isInterrupted()) {
            try {
                if (socketChannel == null || !socketChannel.isOpen()) {
                    if (connectMaster()) {
                        continue;
                    }
                }
                selector.select();
                for (SelectionKey key : selector.selectedKeys()) {
                    if (key.isConnectable()) {
                        socketChannel.finishConnect();
                        socketChannel.register(selector, SelectionKey.OP_READ);
                    } else if (key.isReadable()) {
                        processReadEvent();
                    }
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
        shutdown();
    }

    public void shutdown() {
        closeMaster();
    }
}
