    private void initializeSocketChannel(AsyncCompletionHandler<Void> handler, Queue<SocketAddress> socketAddressQueue) {
        try {
            AsynchronousSocketChannel channel = group == null ? AsynchronousSocketChannel.open() : AsynchronousSocketChannel.open(group);
            new OpenCompletionHandler(handler, socketAddressQueue, channel).completed(null, null);
