import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.io.IOException;
import java.net.InetSocketAddress;
import java.nio.ByteBuffer;
import java.nio.channels.SelectionKey;
import java.nio.channels.Selector;
import java.nio.channels.SocketChannel;
import java.util.Iterator;
import java.util.List;
import java.util.concurrent.BlockingQueue;
import java.util.concurrent.LinkedBlockingQueue;
import java.util.concurrent.Semaphore;
import java.util.concurrent.TimeUnit;

public class ConnectionWorker implements Runnable {
    private static final Logger logger = LoggerFactory.getLogger(ConnectionWorker.class);
    private volatile boolean stop;
    private Selector selector;
    private BlockingQueue<ConnectTask> myConnectQueue = new LinkedBlockingQueue<>();
    private BlockingQueue<WriteTask> myWriteQueue = new LinkedBlockingQueue<>();
    private Semaphore mySemaphore = new Semaphore(1);
    private List<KeyProcessor> myKeyProcessors;
    private TimeService myTimeService;
    private long lastCleanupTime;
    private int mySelectorTimeoutMillis;
    private long myCleanupTimeoutMillis;
    private CleanupProcessor myCleanupProcessor;
    private NewConnectionAllower myNewConnectionAllower;

    public ConnectionWorker(List<KeyProcessor> keyProcessors, TimeService timeService, int selectorTimeoutMillis, long cleanupTimeoutMillis, CleanupProcessor cleanupProcessor, NewConnectionAllower newConnectionAllower) throws IOException {
        this.myKeyProcessors = keyProcessors;
        this.myTimeService = timeService;
        this.mySelectorTimeoutMillis = selectorTimeoutMillis;
        this.myCleanupTimeoutMillis = cleanupTimeoutMillis;
        this.myCleanupProcessor = cleanupProcessor;
        this.myNewConnectionAllower = newConnectionAllower;
        this.selector = Selector.open();
    }

    @Override
    public void run() {
        while (!stop) {
            try {
                connectToPeersFromQueue();
                processSelectedKeys();
                processWriteTasks();
                if (needRunCleanup()) {
                    cleanup();
                }
            } catch (IOException e) {
                logger.error("Error in ConnectionWorker", e);
            }
        }
        closeSelector();
    }

    private void connectToPeersFromQueue() {
        ConnectTask task;
        while ((task = myConnectQueue.poll()) != null) {
            try {
                SocketChannel channel = SocketChannel.open();
                channel.configureBlocking(false);
                channel.connect(new InetSocketAddress(task.getHost(), task.getPort()));
                channel.register(selector, SelectionKey.OP_CONNECT, task);
            } catch (IOException e) {
                logger.error("Failed to connect to peer", e);
            }
        }
    }

    private void processSelectedKeys() throws IOException {
        selector.select(mySelectorTimeoutMillis);
        Iterator<SelectionKey> keys = selector.selectedKeys().iterator();
        while (keys.hasNext()) {
            SelectionKey key = keys.next();
            keys.remove();
            processSelectedKey(key);
        }
    }

    private void processSelectedKey(SelectionKey key) {
        try {
            if (key.isConnectable()) {
                completeConnection(key);
            } else if (key.isWritable()) {
                processWrite(key);
            } else if (key.isReadable()) {
                processRead(key);
            }
        } catch (IOException e) {
            logger.error("Error processing key", e);
            closeChannel(key);
        }
    }

    private void completeConnection(SelectionKey key) throws IOException {
        SocketChannel channel = (SocketChannel) key.channel();
        if (channel.finishConnect()) {
            channel.register(selector, SelectionKey.OP_WRITE);
            ConnectTask task = (ConnectTask) key.attachment();
            task.onConnected(channel);
        } else {
            closeChannel(key);
        }
    }

    private void processWrite(SelectionKey key) throws IOException {
        SocketChannel channel = (SocketChannel) key.channel();
        WriteTask task = (WriteTask) key.attachment();
        ByteBuffer buffer = ByteBuffer.wrap(task.getData());
        channel.write(buffer);
        if (!buffer.hasRemaining()) {
            channel.register(selector, SelectionKey.OP_READ);
            task.onWritten(channel);
        }
    }

    private void processRead(SelectionKey key) throws IOException {
        SocketChannel channel = (SocketChannel) key.channel();
        ByteBuffer buffer = ByteBuffer.allocate(1024);
        int bytesRead = channel.read(buffer);
        if (bytesRead == -1) {
            closeChannel(key);
        } else {
            buffer.flip();
            byte[] data = new byte[buffer.remaining()];
            buffer.get(data);
            // Handle read data
        }
    }

    private void closeChannel(SelectionKey key) {
        try {
            key.channel().close();
        } catch (IOException e) {
            logger.error("Error closing channel", e);
        }
        key.cancel();
    }

    private void closeSelector() {
        try {
            selector.close();
        } catch (IOException e) {
            logger.error("Error closing selector", e);
        }
    }

    public boolean offerConnect(ConnectTask task, int timeout, TimeUnit unit) throws InterruptedException {
        return addTaskToQueue(task, timeout, unit, myConnectQueue);
    }

    private <T> boolean addTaskToQueue(T task, int timeout, TimeUnit unit, BlockingQueue<T> queue) throws InterruptedException {
        return queue.offer(task, timeout, unit);
    }

    private void processWriteTasks() {
        WriteTask task;
        while ((task = myWriteQueue.poll()) != null) {
            try {
                SocketChannel channel = task.getChannel();
                if (channel.isOpen() && channel.isConnected()) {
                    channel.register(selector, SelectionKey.OP_WRITE, task);
                }
            } catch (ClosedChannelException e) {
                logger.error("Channel closed while trying to write", e);
            }
        }
    }

    private void cleanup() {
        long currentTime = myTimeService.getCurrentTimeMillis();
        if (currentTime - lastCleanupTime >= myCleanupTimeoutMillis) {
            myCleanupProcessor.cleanup(selector.keys());
            lastCleanupTime = currentTime;
        }
    }

    private boolean needRunCleanup() {
        long currentTime = myTimeService.getCurrentTimeMillis();
        return currentTime - lastCleanupTime >= myCleanupTimeoutMillis;
    }

    public void shutdown() {
        stop = true;
    }
}
